--create database qlsach
go
use qlsach
go

CREATE TABLE ctpm (
  MaPM nvarchar(10),
  MaSach nvarchar(10) NOT NULL,
  NgayTra date DEFAULT NULL,
  TinhTrangSach int NOT NULL,
  TinhTrangTra int DEFAULT NULL,
  NguoiDung nvarchar(10)  DEFAULT NULL,
  GhiChu nvarchar(50)  DEFAULT NULL,
  TienPhat int NULL
  PRIMARY KEY (MaPM,MaSach)
) 

select MaPM,MaSach,NgayTra,TinhTrangSach,NguoiDung,TinhTrangTra,GhiChu from ctpm where MaPM=N'PM01' and MaSach=N'MS01'
--(N'PM01', N'MS02', '2020-11-17', 1, 2, N'Tuyen',null,0)
select * from ctpm
CREATE TABLE docgia (
  MaDG nvarchar(10)  NOT NULL,
  TenDG nvarchar(20)  NOT NULL,
  SDT nvarchar(20)  NOT NULL,
  DiaChi nvarchar(50)  NOT NULL,
  GioiTinh nvarchar(10)  NOT NULL,
  MatSach int NOT NULL
  PRIMARY KEY (MaDG)
)
select * from ctpm where NgayTra between '2021-07-01 00:00:00' and '2021-07-31 23:59:00'

CREATE TABLE phieumuon (
  MaPM nvarchar(10)  NOT NULL,
  MaDG nvarchar(10)  NOT NULL,
  NgayMuon date NOT NULL,
  NgayHenTra date NOT NULL,
  SoLuongMuon int NOT NULL,
  NguoiDung nvarchar(10)  NOT NULL
  PRIMARY KEY (MaPM)
) 

