CREATE TABLE sach (
  MaSach nvarchar(10)  NOT NULL,
  TenSach nvarchar(500)  NOT NULL,
  TenTG nvarchar(500)  NOT NULL,
  NhaXB nvarchar(500)  NOT NULL,
  TheLoai nvarchar(500)  NOT NULL,
  SoLuong int NOT NULL,
  GiaTien int NOT NULL,
  ImageUrl nvarchar(500)  DEFAULT NULL,
  MieuTa nvarchar(2500)  DEFAULT NULL

  PRIMARY KEY (MaSach)
)

INSERT INTO sach (MaSach, TenSach, TenTG, NhaXB, TheLoai, SoLuong, GiaTien, ImageUrl, MieuTa) VALUES
(N'MS01', N'GIÁO TRÌNH NHỮNG NGUYÊN LÝ MÁC - LÊNIN', N'Bộ Giáo Dục và Đào Tạo', N'Chính Trị Quốc Gia Sự Thật', N'Giáo trình', 10, 59000, '/img/sach/mac.jpg', N'Thực hiện các Nghị quyết của Đảng Cộng sản Việt Nam, nhất là Nghị quyết Trung ương 5 (khóa X) về công tác tư tưởng, lý luận và báo chí trước yêu cầu mới, ngày 18/9/2008, Bộ Giáo dục và Đào tạo đã ban hành Quyết định số 52/2008/QĐ-BGĐT ban hành Chương trình môn học Những Nguyên Lý Cơ Bản Của Chủ Nghĩa Mác - Lênin dành cho sinh viên khối không chuyên ngành Mác - Lênin, tư tưởng Hồ Chí Minh và phối hợp với Nhà xuất bản Chính trị quốc gia Sự thật xuất bản Giáo Trình Những Nguyên Lý Cơ Bản Của Chủ Nghĩa Mác - Lênin (Dành cho sinh viên đại học, cao đăng khối không chuyên ngành Mác - Lênin, tư tưởng Hồ Chí Minh)  Cuốn giáo trình chính trị này bao gồm 3 phần: • Phần 1: Thế giới quan, phương pháp luận triết học của chủ nghĩa Mác - Lênin • Phần 2: Học thuyết kinh tế của chủ nghĩa Mác - Lênin về phương thức sản xuất tư bản chủ nghĩa • Phần 3: Lý luận của chủ nghĩa Mác - Lênin về chủ nghĩa xã hội'),
(N'MS02', N'ĐẠI SỐ TUYỂN TÍNH', N'Ninh Quang Hải', N'NXB Xây Dựng', N'Giáo trình', 10, 52000, '/img/sach/aHR0cHM6Ly9zYWx0LnRpa2ljZG4uY29tL3RzL3Byb2R1Y3QvNTMvMGIvMGUvMjIyYzU0NTU0ODk3ZmE0NzFmOGExNzhlODU4NjJmNmIuanBn.jpg', N'Sách ĐẠI SỐ TUYẾN TÍNH gồm các chương sau:  Chương 1: MA TRẬN - ĐỊNH THỨC - HỆ PHƯƠNG TRÌNH TUYẾN TÍNH.    Chương 2: KHÔNG GIAN VÉC TƠ Chương 3: ÁNH XẠ TUYẾN TÍNH '),
(N'MS03', N'LUẬT IM LẶNG', N'Mario Puzo', N'NXB Văn Học', N'Văn học nước ngoài', 10, 710000, '/img/sach/luat-im-lang-bia-1.jpg', N'“Tổ chức Mafia củng cố uy quyền bằng luật omerta nghĩa là làm thinh, câm nín. Dân quê Sicily kín miệng đến nỗi người lạ hỏi đường cũng làm thinh. Trong nội bộ Mafia có tội nào đáng chết bằng tội mật báo cảnh sát? Dù chỉ đi thưa lính, tố cáo một thằng vừa giết hụt mình hay vừa đánh đập mình có thương tích. Sau cùng luật omerta trở thành một đạo sống của mọi giới. Chồng con bị giết, con gái bị hãm hiếp, người đàn bà chính gốc Sicily chẳng bao giờ đi thưa lính, nhờ nhà nước giải quyết. Vì nhà nước không có quyền đó. Phải là Mafia!”  Cái luật im lặng cổ xưa ấy chính là bức tường thành của Mafia suốt mấy trăm năm. Nhưng đó là ở nơi đất máu Sicily nhiều năm trước. Trên đất Mỹ, khi nhân loại sắp bước sang thiên niên kỉ mới, liệu omerta có còn cái quyền năng vô song để bảo vệ các ông trùm?  Nếu như  Bố Già  nói về Mafia có vẻ hơi cổ điển thì Luật Im Lặng hiện đại hơn, có sự xuất hiện của cả FBI và chính phủ một cách rõ ràng.  Những âm mưu, thủ đoạn, những cuộc đấu trí đấu lực của các phe phái và tổ chức được đề cập đến cho độc giả thỏa mãn trí tưởng tượng, cùng phiêu lưu với nhân vật.  Bạn đọc có thể đọc thêm các tác phẩm khác trong  tuyển tập Mario Puzo  để cùng sống với nhân vật đến giay phút cuối cùng nhé.'),
(N'MS04', N'TUỔI TRẺ HOANG DẠI', N'Nguyễn Ngọc Thạch', N'NXB Văn học', N'Kỹ năng sống', 10, 95000, '/img/sach/b1cb8713c960e18c41bef97120faf749.jpg', N'Một cuốn sách đơn giản của Nguyễn Ngọc Thạch, đặt sự hữu dụng tính tích cực lên hàng đầu. Sách bao gồm nhiều bài viết ngắn, nói lên cách nhìn nhận và suy nghĩ về cuộc sống, gia đình, công việc, cũng như cơn trầm cảm và khủng hoảng nửa đời người của tác giả, một người vừa đi qua tuổi trẻ và vào giai đoạn thứ ba của cuộc đời.Sách được chia làm ba phần, cấu trúc rõ rệt.Phần một, là các bài viết mang tính kỹ năng mềm cho các bạn học sinh, sinh viên và các bạn trẻ mới bắt đầu trên con đường đi làm. Nó là quá trình đúc kết từ thực tế của Thạch trong hơn mười năm đi làm, từ vị trí nhân viên phục vụ nhà hàng cho đến giám đốc một công ty.Đó là các bài viết về gởi email, viết CV nhìn cho hấp dẫn, những chia sẻ về kinh nghiệm đi phỏng vấn xin việc lần đầu, những vui buồn khi còn là sinh viên và nhận công việc phục vụ quán ăn cho đến những lưu ý khi bước chân vào môi trường công sở. Những kỹ năng mềm cần thiết cho sinh viên nhưng ít trường đại học nào dạy.Phần hai, là những suy nghĩ về mối quan hệ giữa người với người và con người với xã hội. Thạch nói lên suy nghĩ của mình về câu hỏi, “Nhiều tiền để làm gì?”, về cách dùng đồng tiền làm sao cho vui. Về chuyện con người ta cứ phải sống theo đánh giá tiêu chuẩn của người xung quanh, của ông hàng xóm mà chưa quen với việc tự hỏi bản thân mình có hạnh phúc thật sự hay không. Về mối quan hệ bạn bè, đi du lịch cùng nhau hay chỉ đơn giản là… cho mượn tiền rồi làm sao đòi lại?Phần cuối cùng, là giai đoạn khủng hoảng nửa đời người của Thạch, khi qua ba mươi, người ta dễ lạc lối và hỏi bản thân mình rằng sẽ phải làm gì tiếp theo, khó tìm kiếm niềm vui trong cuộc sống. Và khi đó, con người ta phải học cách sống đơn giản hơn để cân bằng chính cảm xúc của mình.'),
(N'MS05', N'3 PHÚT SƠ CỨU', N'Ngô Đức Hùng', N'NXB Thế giới', N'Chăm sóc sức khỏe', 10, 125000, '/img/sach/ba-phut-so-cuu-bia.jpg', N'3 phút là khoảng thời gian để cơ thể tiết ra adrenalin, hormon sống còn giúp máu về tim tốt hơn. Nếu bạn sơ cứu không đúng cách, máu về tim sẽ tồi hơn và các tế bào sẽ rối loạn chuyển hóa.3 phút là thời gian chảy máu trước khi cơ thể khởi động quá trình cầm máu. Nếu bạn sơ cứu không đúng cách, quá trình cầm máu sẽ tồi hơn và giết chết các tế bào được mạch máu đó nuôi dưỡng.3 phút là thời gian tế bào não có thể chịu đựng được khi thiếu oxy trước khi tổn thương vĩnh viễn. Nếu bạn sơ cứu không đúng cách, tế bào não sẽ tổn thương vĩnh viễn và lúc ấy các can thiệp y khoa sẽ trở thành vô nghĩa.Cuốn sách gồm đầy đủ các thông tin giúp bạn sơ cứu trong 3 phút đó. Ngắn gọn. Rõ ràng. An toàn. Hiệu quả'),
(N'MS06', N'CẤP TỐC 789+ ÔN THI THPT MÔN ANH', N'Phạm Thị Tâm', N'Đại Học Quốc Gia Hà Nội', N'Sách Luyện Thi THPT Quốc Gia', 10, 180000, '/img/sach/sach-cap-toc-789-tong-on-thi-thpt-quoc-gia-mon-anh_L.jpg', N'Sách được chia làm 4 phần:  - Phần 1: Quét nhanh KIẾN THỨC TRỌNG TÂM - Phần 2: Tổng hợp skill, mẹo, tips... x2, x3 tốc độ làm, giải đề - Phần 3: CHIẾN THUẬT TỔNG ÔN - MỤC TIÊU ĐIỂM 8 9 - Phần 4: Bộ câu hỏi vận dụng - vận dụng cao chinh phục điểm 10  Sau khi các bạn hoàn thành cuốn sách CẤP TỐC 7 8 9+ học sinh sẽ được trang bị đầy đủ kiến thức bám sát chương trình tinh giản 2020 của Bộ GD&ĐT, kỹ năng bấm máy tính Casio giải nhanh câu hỏi chi với 20s/1 câu, Tips, mẹo, phương pháp giải nhanh hiệu quả rõ rệt. Ngoài ra, các em học sinh nên xem thêm cuốn sách trong bộ \"CẤP TỐC 789+\" Môn Lý - Hoá - Anh giải pháp cứu cánh vạn sĩ tử 2002.'),
(N'MS07', N'TÂM LÝ HỌC TÍCH CỰC', N'Edward Hoffman', N'NXB Lao Động', N'Kỹ năng sống', 10, 129000, '/img/sach/tam-ly-hoc-tich-cuc.jpg', N'Tâm Lý Học Tích Cực là một cuốn sách thực hành về sự phát triển cá nhân và nuôi dưỡng niềm vui, thông qua cách tiếp cận của tâm lý học tích cực và được thiết kế đặc biệt để tổng hợp các chủ đề theo từng chương ngắn gọn và chú trọng tính vận dụng. Cuốn sách bao gồm 60 nội dung độc lập và súc tích để giúp người đọc tối ưu hóa sự phát triển cá nhân, nhằm làm chủ một cuộc sống hạnh phúc hơn, trọn vẹn hơn và có ý nghĩa hơn.  Trong 20 năm tồn tại với tư cách một chuyên ngành học thuật mới, tâm lý học tích cực (positive psychology) đã nhận được rất nhiều sự quan tâm. Chuyên ngành tâm lý học tích cực tập trung nghiên cứu khoa học về hạnh phúc và sự phồn thịnh. Trên toàn thế giới, không khó để tìm thấy những tác phẩm nghiên cứu xoay quanh chủ đề này.  Khi lĩnh vực tâm lý học tích cực phát triển, các khái niệm của nó được mở rộng đáng kể và hiện nay đã bao gồm cả những khía cạnh hoạt động lành mạnh giữa các cá nhân, vợ chồng, gia đình, các tổ chức và những cộng đồng lớn hơn. Khi giảng dạy những khái niệm hấp dẫn này, bộ đôi tác giả thường kết hợp cả hoạt động trong lớp và bài tập về nhà để nâng cao chất lượng học tập của học viên. Phản hồi của học viên về các hoạt động thực hành tâm lý học tích cực luôn đầy khích lệ. “Nó đã giúp tôi thay đổi cuộc sống” là phản hồi phổ biến nhất - thường xuyên tới mức các tác giả đã quyết định viết cuốn sách này. Tâm Lý Học Tích Cực tổng hợp các chủ đề theo từng chương ngắn gọn và mạch lạc, chẳng hạn như hạnh phúc mang tính chủ quan, tình yêu lãng mạn, gia đình tích cực, hay hạnh phúc suốt đời. Các tác giả cũng cung cấp một số biện pháp tác động “đặc thù” đến sự phồn thịnh trong tâm lý học tích cực, như các nội dung về lòng biết ơn và cách hình dung tốt nhất về bản thân mình.'),
(N'MS08', N'QUY TẮC 5 GIÂY', N'Mel Robbins', N'NXB Thế Giới', N'Kỹ năng sống', 10, 135000, '/img/sach/quy-tac-5-giay-nha-nam.jpg', N'Quy Tắc 5 Giây giới thiệu quy tắc thúc đẩy bản thân để biến chuyển cuộc đời, sự nghiệp và sự tự tin chỉ trong 5 giây.  “Nếu bạn đang tìm người giúp thay đổi cuộc đời mình, hãy nhìn vào gương!” Đây quả là một tuyên bố táo bạo của tác giả Mel Robbins, cũng như những lời sau đó còn táo gan hơn của bà, rằng, “Bạn sắp được học một điều phi thường – đó là bạn chỉ cần năm giây để thay đổi cuộc đời mình”.  Thông qua cuốn sách này, tác giả nổi tiếng, diễn giả nổi tiếng của chương trình TED, doanh nhân thành đạt Mel Robbins sẽ lý giải về Quy tắc 5 giây mà cô sáng tạo ra, đồng thời chỉ ra cho chúng ta cách chỉ tốn 5 giây để: - Trở nên tự tin - Phá vỡ thói quen chần chừ và nghi ngờ bản thân - Đánh bại nỗi sợ hãi và sự không chắc chắn - Ngừng lo lắng và cảm thấy hạnh phúc hơn - Chia sẻ ý tưởng với lòng can đảm  Quy tắc 5 giây là một giải pháp đơn giản, phù hợp với tất cả các vấn đề cho một vấn đề mà tất cả chúng ta phải đối mặt – sự trì hoãn.  Như tác giả Mel Robbins đã nói, bí mật không phải là biết điều gì phải làm, mà là biết cách khiến bản thân làm điều đó.  Quy Tắc 5 Giây ( Nhã Nam) sẽ giúp các bạn thoát khỏi sức ỳ của bản thân, vượt qua cú sốc cá nhân để sống bình thường, cân bằng cuộc sống...'),
(N'MS09', N'PHƯƠNG PHÁP TẠO ĐỘNG LỰC TRONG CÔNG VIỆC', N'Jon Sternfeld', N'NXB Lao Động', N'Kỹ năng sống', 10, 158000, '/img/sach/phuong-phap-tao-dong-luc-trong-cong-viec.jpg', N'Thể thao và kinh doanh là hai lĩnh vực tưởng như không có điểm giao nhau nhưng thực ra, có những bài học thành công trong thể thao khi áp dụng vào việc kinh doanh lại mang đến những kết quả ngoài mong đợi.  Phương Pháp Tạo Động Lực Trong Công Việc không đơn thuần là một cuốn sách đưa ra những nguyên tắc thành công khô khan, nó là tổng hợp những bài học, kinh nghiệm thành công tới từ những huấn luyện viên và vận động viên bóng rổ hàng đầu thế giới. Người thành công và khao khát thành công luôn duy trì cho mình tính kỷ luật trong mọi hoàn cảnh mà không mưu cầu sự chú ý, khen thưởng, tán thành từ những người xung quanh. Họ làm vậy chỉ bởi duy nhất một mục đích: khiến bản thân trở nên hoàn thiện hơn qua từng ngày, bằng những việc tưởng chừng nhỏ bé mà nhiều người sẵn sàng bỏ qua. Họ không ngừng thiết lập kế hoạch, điều chỉnh hành động và duy trì thói quen. Những người đứng đầu trong giới thể thao và kinh doanh đã chứng minh rằng thành công là kết quả của mỗi việc nhỏ chúng ta làm hằng ngày, và “Cách chúng ta làm một việc là cách chúng ta làm mọi việc”.'),
(N'MS10', N'CHẠM', 'Werner Bartens', N'NXB Thanh Niên', N'Kỹ năng sống', 10, 122000, '/img/sach/cham-az-viet-nam.jpg', N'Nói đến sự đụng chạm, hầu hết mọi người sẽ cảm thấy khó chịu và nghĩ rằng đó là hành động bất thường làm phá vỡ mọi giới hạn khoảng cách. Tùy vào nền văn hóa của từng nước, ví dụ như ở châu Âu thì đó là một hành động hết sức thân mật và thể hiện sự thân thiện, lịch sự đối với người khác khi gặp gỡ chào hỏi nhau; nhưng với văn hóa phương Đông thì họ cho rằng sự đụng chạm là điều kiêng kỵ và có thể khiến đối phương cảm thấy ghê tởm, xấu hổ hoặc tổn thương.  Thế nhưng, với cuốn sách Chạm, những định kiến về việc “chạm” và “được chạm” từ trước đến giờ sẽ được nhìn nhận theo góc độ khoa học, được các nhà nghiên cứu chỉ ra bằng những ví dụ chân thực và rõ ràng. “Tiếp xúc vật lý là ngôn ngữ đầu tiên chúng ta sử dụng, nhưng có quá ít người biết điều này.” Ngày nay, nhiều công trình nghiên cứu khoa học về xúc giác đã chỉ ra rằng việc tiếp xúc thân thể có thể kích hoạt tất cả những gì và mức độ hữu ích, quý giá mà chúng đem lại - dù trong lĩnh vực y học hay trong tình yêu - hôn nhân, công việc và đương nhiên đối với cả đời sống giải trí, thể thao, văn hóa,… Thậm chí trong nhiều ngôn ngữ, không phải ngẫu nhiên mà “được chạm” lại mang hai ý nghĩa tuyệt vời. Nghĩa đen chính là việc được tiếp xúc thân thể, còn nghĩa ẩn là việc “chạm” đến tâm hồn, khi con người ta bị choáng ngợp bởi cảm xúc, thấy cảm động bởi một khoảnh khắc hay được thấu hiểu trong một cuộc trò chuyện.'),
(N'MS100', N'Những Nhà Kinh Tế Tiên Phong', N'Nathan Schneider', N'NXB Công Thương', N'Quản lý kinh doanh', 10, 139000, '/img/sach/nhung-nha-kinh-te-tien-phong.jpg', N'Một chế độ phong kiến kiểu mới đang trỗi dậy ở phạm vi toàn cầu, với những tập đoàn độc quyền trao tặng chiến lợi phẩm cho người giàu, còn các thường dân thì sống một cuộc đời bất ổn định và đầy biến động. Nhưng trong cuốn sách “Những nhà kinh tế tiên phong”, tác giả Nathan Schneider đã chỉ ra rằng có một phương án thay thế đã xuất hiện, chỉ chờ chúng ta khám phá và tận dụng. Đó chính là tiến hành những mô hình hợp tác.  Một nền kinh tế cấp tiến mới sẽ được hình thành trong tương lai gần, và nó bén rễ từ truyền thống hợp tác đã luôn trao sức mạnh cho con người trong hàng thế kỷ. Chủ nghĩa hợp tác đang được hồi sinh, trả lại quyền làm chủ, quyền giám sát và quyền điều hành cho người dân - những người vừa là chủ sở hữu, vừa là người quản trị, đồng thời là người cấp vốn, cũng như là người tiêu dùng kiêm thành viên của những liên minh hoạt động vì lợi ích chung.  Các tổ chức hợp tác là những doanh nghiệp thuộc sở hữu chung của mọi người, được vận hành một cách dân chủ nhằm cải thiện lợi ích về kinh tế, xã hội và văn hóa của các thành viên. Những liên minh này thường nổi lên trong những giai đoạn khủng hoảng, giúp người dân có tiếng nói tại nơi làm việc, cũng như tại các tổ chức liên hiệp tín dụng, dịch vụ y tế, đơn vị cung cấp tiện ích công... - những nơi mà con người luôn dựa vào để duy trì một cuộc sống tốt đẹp.'),
(N'MS101', N'Mưu Lược Trong Kinh Doanh', N'Hoàng Văn Tuấn', N'NXB Lao Động', N'Quản lý kinh doanh', 10, 130000, '/img/sach/muu-luoc-trong-kinh-doanh.jpg', N'Từ xưa tới nay, thương trường luôn là nơi diễn ra những cuộc cạnh tranh không khoan nhượng. Để giành thắng lợi, bên cạnh tiềm lực kinh tế và kiến thức chuyên môn, cần phải có bề dày kinh nghiệm – điều mà các doanh nhân trẻ không thể có trong một sớm một chiều. Trong trường hợp này, cách tích lũy nhanh nhất chính là tích lũy từ truyền thống, từ kho tàng kinh nghiệm quý báu mà cổ nhân đã đúc kết.  Cuốn sách Mưu Lược Trong Kinh Doanh cung cấp cho các doanh nhân những kinh nghiệm của cổ nhân, được đúc kết dưới dạng cô đọng đầy hình ảnh với những ví dụ sống động từ chính thương trường, sẽ mang lại cho bạn đọc nhiều kiến thức bổ ích trong kinh doanh.'),
(N'MS102', N'Quản Lý Chuyên Nghiệp', N'Dottie Schindlinger', N'NXB Lao Động', N'Quản lý kinh doanh', 15, 119000, '/img/sach/quan-ly-chuyen-nghiep.jpg', N'Tác động mạnh mẽ và không thể đoán trước của sự đổi mới và gián đoạn công nghệ, kinh tế và xã hội, cùng các quy định phức tạp của chính phủ…những yếu tố này đã làm cho việc quản lý doanh nghiệp ngày nay trở nên khó khăn hơn. Mặc dù có nhiều trở ngại, nhưng hội đồng quản trị và các giám đốc doanh nghiệp vẫn có khả năng phản ứng ngay lập tức và chính xác trong mọi tình huống cho dù có phức tạp đến đâu. “Quản lý chuyên nghiệp” là một cuốn sách hướng dẫn các phương pháp tiếp cận và quy trình công nghệ mới để giúp cho công việc quản lý trở nên dễ dàng hơn.  Trong khi hầu hết các sách quản lý doanh nghiệp tập trung vào các nguyên tắc và thực tiễn đã được thiết lập, thì cuốn sách này lại nhấn mạnh những sự thay đổi trong cách thức thực hành cùng với sự tiến bộ nhanh chóng của công nghệ. Dựa trên kinh nghiệp làm việc của các tác giả với hơn 15.000 tổ chức và 500.000 giám đốc trên toàn cầu, cuốn sách “Quản lý chuyên nghiệp” đã chỉ ra giải pháp để các công ty có thể thay đổi cách họ hình thành và ban hành các quy định trong quản lý doanh nghiệp nhằm hướng tới một sự phát triển bền vững.'),
(N'MS103', N'CONTENT ĐẮT CÓ BẮT ĐƯỢC TREND', 'Ryan Wakeman', N'NXB Công Thương', N'Quản lý kinh doanh', 10, 119000, '/img/sach/content-dat-co-bat-duoc-trend.jpg', N'Có bốn yếu tố then chốt cần được cân nhắc với trước khi tiến hành bất kỳ chiến dịch truyền thông nào. Đó là:  - Mục tiêu - Tính hiệu quả - Xu hướng - Tỷ suất hoàn vốn  Chúng ta đang sống trong thời đại mà để có thể theo kịp trong không gian truyền thông số, tất cả mọi thứ cần quan tâm là Content, Content và Content. Điều này đặt rất nhiều áp lực lên bộ phận truyền thông và nhiều áp lực hơn nữa lên chính bản thân bạn nếu bạn là chủ một doanh nghiệp nhỏ.  Nhưng ngày này, để có thể theo kịp sự cạnh tranh trong tất cả các không gian truyền thông khác nhau, tỷ lệ mà bạn cần tạo ra những content chất lượng quả là điên rồ. Thậm chí, có những người còn nói rằng họ phải sản xuất ra 50 bài content mỗi ngày chỉ để đăng tải lên các tài khoản thương hiệu cá nhân. Điều này nghe có vẻ điên rồ, nhưng hãy thử chia nhỏ con số trên ra và bạn sẽ thấy rằng nó hoàn toàn hợp lý…  - 2 bài đăng trên Facebook - 2 tấm hình đăng trên Instagram - 3 bài đăng trên Twitter - Các bài blog đăng tải trên chính trang web của bạn cần có hình ảnh thu nhỏ, nội dung,… - 1 bài blog trên trang của người khác, hay tương tự vậy… - Một vài bài đăng trên Instagram Stories - 1 bài viết đăng trên LinkedIn.'),
(N'MS104', N'Đế Chế Alibaba', N'Trần Vĩ', N'NXB Hồng Đức', N'Quản lý kinh doanh', 10, 172000, '/img/sach/de-che-alibaba-bia.jpg', N'Khi khởi đầu, Alibaba thiếu vốn, công nghệ, và kế hoạch hoạt động. Những khó khăn này như báo hiệu cho một thất bại nhãn tiền. Nhưng tầm nhìn của Jack Ma cho phép ông mơ giấc mơ lớn, cái giúp doanh nghiệp này trở thành một kinh doanh khổng lồ. Như ông đã chia sẻ, mục tiêu cuối cùng là “đánh bại eBay, thâu tóm Yahoo và cản bước tiến của Google”. Phong cách lãnh đạo đầy cảm hứng của Jack Ma đã để lại tầm ảnh hưởng sâu rộng cho rất nhiều doanh nghiệp non trẻ mới bước đầu khởi nghiệp. Nhìn chung, phong cách quản trị nhân sự của Jack Ma được tóm gọn trong những nguyên tắc chính sau:  Bí mật quản trị nhân lực để tạo ra một đội quân bách chiến bách thắng Văn hóa doanh nghiệp Nhìn chung, Khi doanh nghiệp phát triển đến một giai đoạn nhất định, xây dựng văn hóa doanh nghiệp sẽ trở thành mấu chốt để nhà quản lí đạt được sự đồng lòng nơi tập thể nhân viên. Với Alibaba xây dựng nên chín tôn chỉ và sáu quan niệm nòng cốt chính thức ghi vào quy định, cụ thể hóa thành hệ thống nguyên tắc sát hạch hành vi, đó là thành quả của tổng kết quá trình “Văn hóa doanh nghiệp” chính là sợi dây kết nối Ali: văn hóa tươi cười, văn hóa võ hiệp, văn hóa đối lập'),
(N'MS105', N'Muôn Kiếp Nhân Sinh', N'Nguyên Phong', N'NXB Tổng Hợp TP.HCM', N'Kiến thức bách khoa', 10, 228000, '/img/sach/muon-kiep-nhan-sinh-many-times-many-lives.jpg', N'Sách Muôn Kiếp Nhân Sinh là tác phẩm do Giáo sư John Vũ - Nguyên Phong viết từ năm 2017 và hoàn tất đầu năm 2020 ghi lại những câu chuyện, trải nghiệm tiền kiếp kỳ lạ từ nhiều kiếp sống của người bạn tâm giao lâu năm, ông Thomas – một nhà kinh doanh tài chính nổi tiếng ở New York. Những câu chuyện chưa từng tiết lộ này sẽ giúp mọi người trên thế giới chiêm nghiệm, khám phá các quy luật về luật Nhân quả và Luân hồi của vũ trụ giữa lúc trái đất đang gặp nhiều tai ương, biến động, khủng hoảng từng ngày.   Muôn Kiếp Nhân Sinh là một bức tranh lớn với vô vàn mảnh ghép cuộc đời, là một cuốn phim đồ sộ, sống động về những kiếp sống huyền bí, trải dài từ nền văn minh Atlantis hùng mạnh đến vương quốc Ai Cập cổ đại của các Pharaoh quyền uy, đến Hợp Chủng Quốc Hoa Kỳ ngày nay.    Muôn Kiếp Nhân Sinh cung cấp cho bạn đọc kiến thức mới mẻ, vô tận của nhân loại lần đầu được hé mở, cùng những phân tích uyên bác, tiên đoán bất ngờ về hiện tại và tương lai thế giới của những bậc hiền triết thông thái. Đời người tưởng chừng rất dài nhưng lại trôi qua rất nhanh, sinh vượng suy tử, mong manh như sóng nước. Luật nhân quả cực kỳ chính xác, chi tiết, phức tạp được thu thập qua nhiều đời, nhiều kiếp, liên hệ tương hỗ đan xen chặt chữ lẫn nhau, không ai có thể tính được tích đức này có thể trừ được nghiệp kia không, không ai có thể biết được khi nào nhân sẽ trổ quả. Nhưng, một khi đã gây ra nhân thì chắc chắn sẽ gặt quả - luật Nhân quả của vũ trụ trước giờ không bao giờ sai.'),
(N'MS106', N'Câu Đố Việt Nam', N'Mai Chi', N'NXB Hồng Đức', N'Kiến thức bách khoa', 20, 58000, '/img/sach/cau-do-viet-nam-bia-truoc.jpg', N'Câu đố là trò chơi trí tuệ. Thông qua việc đọc và giải đáp các câu đố về hoa quả, cây cối, đồ vật, con vật; câu đố về con người, về các hiện tượng tự nhiên… trẻ em sẽ rèn luyện được óc quan sát tổng hợp, tư duy logic và phát triển trí thông minh. Đọc và giải đáp câu đố còn làm phong phú thêm vốn ngôn ngữ và giúp trẻ nâng cao khả năng phán đoán, giải thích các hiện tượng trong tự nhiên cũng như trong đời sống. Câu đố Việt Nam là một kho tàng kiến thức và tri thức của nhân dân. Nó dạy ta cách quan sát, phán đoán sự vật và các hiện tượng diễn ra quanh ta hoặc trong lịch sử một cách tinh nhạy. Câu đố còn giúp ta vận dụng trí thông minh, sự suy nghĩ và cách ứng phó, đối đáp, lẹ làng, dí dỏm đầy chất hóm hỉnh, linh hoạt với tất cả tấm lòng bao dung, độ lượng trong cách giải trí và quy tụ cộng đồng người lao động, sau ngày làm việc căng thẳng. Do vậy, Câu đố Việt Nam cần được khuyến khích phát triển trong vốn quý của nền văn học dân gian nước nhà. Tự thân câu đố còn bao hàm ý nghĩa về nhân sinh, tính đại chúng, dung dị, ý thức dân chủ, và sự hoà nhập, quy tụ hợp đoàn, rất gắn bó, gần gũi nhau giữa các cộng đồng người với mọi thành phần, lứa tuổi, giới tính trong dịp giải trí vô tư, hồn nhiên vào trò chơi đố và giải đầy lý thú.'),
(N'MS107', N'Giải Mã Siêu Trí Tuệ', N'Vishen Lakhian', N'NXB Công Thương', N'Kiến thức bách khoa', 10, 159000, '/img/sach/giai-ma-sieu-tri-tue-the-code-of-the-extraordinary-mind.jpg', '10 quy tắc gia tăng hạnh phúc, khơi nguồn thức tỉnh và nâng tầm ảnh hưởng  Điều gì sẽ sảy ra nếu con người cứ vô thức sống theo những quan niệm, niềm tin đã lỗi thời về tình yêu, công việc hay hạnh phúc được truyền từ đời này sang đời khác? Và nếu có nhận ra chính mình cũng đang sống như vậy, bạn có dũng cảm vượt ra ngoài khuôn khổ ấy không?  Giải Mã Siêu Trí Tuệ là một bản thiết kế 10 quy tắc giúp bạn phá bỏ xiềng xích của một cuộc sống bình thường để sống một cuộc đời phi thường. Kết hợp tư duy tính toán, lý thuyết tích hợp, tâm linh hiện đại, sinh học tiến hóa cùng với cách dẫn dắt hài hước, Vishen Lakhiani cung cấp công cụ giúp bạn trở thành một kẻ dám đột phá, thách thức, phát triển cá nhân, bẻ khóa tâm trí và tạo ra các quy tắc mới cho cuộc sống CỦA CHÍNH BẠN.  Cuốn sách gửi tới người đọc một bộ quy tắc “mã hóa bản thân” để hiểu về thế giới theo những cách phi thường; để xây dựng một cuộc sống hạnh phúc; và hơn cả, là để dám mơ về những điều vượt ngoài khuôn khổ truyền thống.'),
(N'MS108', N'Khoa Học Thật Là Vui', N'Triệu Từ Húc', N'NXB Thanh Niên', N'Kiến thức bách khoa', 30, 25000, '/img/sach/khoa-hoc-that-la-vui-cuoc-song-cua-mot-nha-du-hanh-vu-tru.jpg', N'Không còn là kiến thức hàn lâm, khó hiểu hay nhàm chán, thế giới khoa học sẽ mở ra trước mắt bé thật vui thật hấp dẫn với sách Khoa Học Thật Là Vui - Cuộc Sống Của Một Nhà Du Hành Vũ Trụ   Bộ sách gồm có 6 cuốn:   Khoa học thật là vui -Tuyệt chiêu thoát thân của động vật  Khoa học thật là vui - Bật mí chuyện sâu hóa bướm  Khoa học thật là vui - Khám phá làn da của loài người  Khoa học thật là vui - Các loại vi khuẩn  Khoa học thật là vui - Bí ẩn của bầu trời  Khoa học thật là vui - Cuộc sống của một nhà du hành vũ trụ'),
(N'MS109', N'Thời Cá Voi Biết Đi', N'Dougal Dixon', N'NXB ĐH Sư Phạm', N'Kiến thức bách khoa', 10, 98000, '/img/sach/thoi-ca-voi-biet-di.jpg', N'Các bạn nhỏ có biết thời cá voi biết đi, thời bò sát mọc ra đôi cánh hay thời mà rắn còn chân?  Dựa trên vết tích hoá thạch của các loài động vật có lịch sử hàng trăm triệu năm trước mà chúng ta có thể phát hiện ra những điều kỳ diệu. Hãy cùng khám phá để tim hiểu về sự phát triển, tiến hoá cho đến khi bị diệt vong của các giống loài. Và trong đó có cả chính chúng ta - con người'),
(N'MS11', N'CHỦ NGHĨA TỐI GIẢN', N'Fields Millburn', N'NXB Lao Động Xã Hội', N'Kỹ năng sống', 10, 139000, '/img/sach/chu-nghia-toi-gian.jpg', N'Chủ Nghĩa Tối Giản được viết và tổ chức để giúp bạn suy nghĩ về cuộc sống của bạn và cách bạn sống; để khiến bạn thực hiện một số công việc và hướng nội để bạn có thể rời khỏi cuộc sống cũ và bắt đầu cuộc hành trình sang một cuộc sống mới; để giúp bạn nhận ra mình có thể thay đổi, bạn có thể chọn lại mẫu người mà bạn sẽ trở thành, bạn có thể trở thành người tốt nhất mà bạn có khả năng trở thành - bạn thực sự, đam mê, yêu thương, từ bi, kỷ luật và hạnh phúc.  JOSHUA FIELDS MILLBURN & RYAN NICODEMUS là những tác giả bán chạy nhất và các diễn giả quốc tế chia sẻ thông điệp của họ về việc sống một cuộc sống có ý nghĩa với ít công cụ hơn. Câu chuyện của họ đã được giới thiệu trên chương trình Today và trên các tạp chí Time, People, Forbes, New York Times, Wall Street Journal, USA Today, Boston Globe, San Francisco Chronicle, Chicago Tribune, Seattle Times, Toronto Star, Globe & Mail, Vancouver Sun, Village Voice, LA Weekly và nhiều phương tiện khác.  Chúng tôi muốn nói với bạn rằng chủ nghĩa tối giản đóng một vai trò quan trọng trong việc sống một cuộc sống có ý nghĩa. Chủ Nghĩa Tối Giản là một công cụ để loại bỏ sự sống dư thừa và tập trung vào các yếu tố cần thiết. Do đó, cuốn sách này là về chủ nghĩa tối giản, bởi vì cuốn sách này tập trung vào năm lĩnh vực thiết yếu của cuộc sống. Bằng cách chấp nhận sự tối giản trong các khía cạnh khác của cuộc sống (tài sản của bạn, công việc của bạn, v.v...), bạn có thể tập trung vào những điều quan trọng nhất trong cuộc sống.'),
(N'MS110', N'Nông Trại Nuôi Sống Chúng Ta', N'Nancy Castaldo', N'NXB ĐH Sư Phạm', N'Kiến thức bách khoa', 10, 120000, '/img/sach/nong-trai-nuoi-song-chung-ta.jpg', N'Thức ăn của chúng ta có đến từ đâu? Nông trại đóng vai trò gì? Như thế nào mới gọi là nông dân?  Hãy theo chân gia đình nhà nông trong một năm và khám phá xem những người nông dân ấy đã nuôi trồng thực phẩm thơm ngon, tươi mới cho chúng ta như thế nào nhé. Hãy khám phá các công việc ở nông trại gia đình, nhừng loài vật, rau màu và máy móc, cũng như nhịp sống của nhà nông.'),
(N'MS111', N'30 Giây Y Học', N'Gabrielle M FiN', N'NXB Kim Đồng', N'Kiến thức bách khoa', 10, 130000, '/img/sach/30-giay-y-hoc.jpg', N'Lời thề Hippocrate là gì? Ai là người đầu tiên đã khám phá ra kháng sinh? Có đúng là công nghệ hiện nay cho phép phẫu thuật tái tạo để sử dụng các phần của cơ thể tùy chỉnh từ công nghệ in 3D? Chụp cắt lớp (CT) khác gì với chụp cộng hưởng từ (MRI)?  30 Giây Y Học sẽ trả lời cho những câu hỏi trên, bằng việc trình bày các cột mốc quan trọng, phương thức điều trị, bước tiến công nghệ, các loại thuốc và bệnh tật. Mỗi mục được tóm tắt chỉ trong 30 giây, với 300 từ và một bức tranh, đưa bạn đọc vào hành trình cuốn hút từ lúc y học còn phụ thuộc vào tự nhiên, đến phẫu thuật nội soi, các chi phỏng sinh học và các đột phá về thuốc điều trị. Minh họa bằng các bức tranh sáng tạo, cung cấp tiểu sử về những nhà tiên phong trong lĩnh vực y học, đây là cuốn sách thiết yếu cho bất kì ai muốn biết về thế giới của các căn bệnh và những người chữa trị chúng.   Chủ biên Gabrielle M. Finn là giảng viên lâu năm tại khoa Giáo dục Y tế trường Y Hull York. Bà là bác sĩ giải phẫu và nhà giáo dục yêu thích việc truyền bá kiến thức rộng rãi ở nhiều lĩnh vực chăm sóc sức khỏe. Bà quan tâm nghiên cứu xây dựng tính chuyên nghiệp của bác sĩ và cơ sở y tế, cũng như giải phẫu học sư phạm. Gabrielle là Chủ tịch Giáo dục của Hiệp hội Giải phẫu. Bà có bằng tiến sĩ ngành Giáo dục Y tế.'),
(N'MS112', N'30 Giây AI Và Robot Học', N'Luis de Miranda', N'NXB Kim Đồng', N'Kiến thức bách khoa', 10, 130000, '/img/sach/30-giay-ai-va-robot-hoc.jpg', N'Phương pháp “học sâu” là gì? Trí thông minh nhân tạo biết suy nghĩ? Thế hệ robot trong tương lai gần sẽ trông thế nào? Liệu có sự phân hóa giữa người am hiểu công nghệ và người sợ hãi nó? Con người và các cỗ máy thông minh rồi sẽ hợp thể thành những chủng loài hoàn toàn mới?  30 Giây AI Và Robot Học sẽ trình bày đáp án súc tích và dễ hiểu cho các câu hỏi trên trong 50 mục, bao gồm các khái niệm cốt lõi và cột mốc lịch sử trong lĩnh vực robot học và trí thông minh nhân tạo (AI). Mỗi mục được gói gọn trong vòng 30 giây – chỉ với 300 từ và một hình ảnh minh họa. Đây là cuốn sách phải đọc dành cho bất kì ai muốn hiểu rõ về các thay đổi quan trọng đang diễn ra trong cuộc sống hàng ngày, tại nơi làm việc và trong chính cơ thể, khối óc của chúng ta. Với việc bổ sung tiểu sử các nhân vật hàng đầu và minh họa bằng đồ họa sáng tạo, 30 Giây  AI và Robot học sẽ giúp độc giả định hướng được trong thế giới của những chiếc máy tính phủ khắp, các đô thị thông minh, robot cộng tác và tiềm lực con người trong kỉ nguyên tự động hóa.  Chủ biên Luis de Miranda là triết gia kiêm sử gia của bộ môn Lịch sử Ý tưởng, người đã thực hiện các nghiên cứu chuyên sâu về người-máy hợp thể học, văn hóa số và sự lệ thuộc vào công nghệ trong cuộc sống thường ngày của chúng ta. Ông là tác giả của một số tác phẩm tiểu thuyết lẫn phi tiểu thuyết như Nghệ thuật của Tự do trong Kỉ nguyên Tự động. Hiện ông đang công tác về AI và Nhân văn số tại đại học Örebro, Thụy Điển.'),
(N'MS113', N'Dấu Ấn Khơi Dòng Văn Hóa Việt', N'Lê Minh Quốc', N'NXB Tổng Hợp TP.HCM', N'Kiến thức bách khoa', 5, 260000, '/img/sach/dau-an-khoi-dong-van-hoa-viet.jpg', N'Văn hóa là một bộ phận không thể tách rời của cuộc sống và nhận thức - một cách hữu thức cũng như vô thức - của các cá nhân và các cộng đồng. Văn hóa là tổng thể sống động các hoạt động tạo trong quá khứ và trong hiện tại.    Qua các thế kỷ, hoạt động sáng tạo ấy đã hình thành nên một hệ thống các giá trị, các truyền thống và các thị hiếu - những yếu tố xác định đặc tính riêng của mỗi dân tộc. Vì vậy văn hóa nhất định sẽ ghi dấu ấn của mình lên hoạt động kinh tế của con người và xác định những mặt mạnh mặt yếu riêng của các quá trình sản xuất trong một xã hội.    Đó là những điều rút ra từ các kinh nghiệm tiêu cực lẫn tích cực của mọi nước. Hễ nước nào tự đặt ra cho mình mục tiêu phát triển kinh tế mà tách rời môi trường văn hóa thì nhất định xẩy ra những mất cân đối nghiêm trọng cả về mặt kinh tế và văn hóa, và tiềm năng sáng tạo của nước ấy sẽ bị suy yếu rất nhiều.    Một sự phát triển chân chính đòi hỏi phải sử dụng một cách tối ưu nhân lực và vật lực của mỗi cộng đồng. Vì vậy phân tích đến cùng, các trọng tâm, các động cơ và các mục đích của phát triển phải được tìm trong văn hóa...'),
(N'MS114', N'Kể Tiếp Chuyện Bác Hồ', N'Trần Quân Ngọc', N'NXB Tổng Hợp TP.HCM', N'Kiến thức bách khoa', 14, 70000, '/img/sach/ke-tiep-chuyen-bac-ho.jpg', N'Năm 2020 chúng ta có nhiều ngày kỷ niệm đáng ghi nhớ. Đó là ngày kỷ niệm lần thứ 130 ngày sinh của Chủ tịch Hồ Chí Minh (19/5/1890 - 19/5/2020); kỷ niệm lần thứ 100 ngày Nguyễn Ái Quốc - Hồ Chí Minh tham dự Đại hội Tua, thành lập Đảng Cộng sản Pháp (30/12/1920); kỷ niệm lần thứ 90 ngày thành lập Đảng Cộng sản Việt Nam (03/02/1930 - 03/02/2020); kỷ niệm 75 năm ngày thành lập nước Việt Nam Dân chủ Cộng hòa (02/9/1945 - 02/9/2020)...   Kể Tiếp Chuyện Bác Hồ bao gồm một số bài viết của tác giả về hai mảng đề tài mà ông quan tâm tìm hiểu trong nhiều năm qua.    Ở mảng đề tài thứ nhất - về những người bạn quốc tế của Bác Hồ - ngoài bài viết về người bạn Pháp là Pôn Vayăng Cutuyriê (trong thời kỳ thành lập Đảng Cộng sản Pháp) và một người bạn Haiti là Max Clanhvin Blôngcua (trong giai đoạn làm báo Le Paria - Người cùng khổ) đã công bố cách đây 30 năm, chúng tôi tập hợp một số bài của tác giả viết về những người bạn khác của Bác như Côlarốp V.P., Đimitrốp G.M, hai người Bungari từng đảm nhiệm trọng trách Tổng Bí thư Quốc tế Cộng sản do Lênin sáng lập; nhà văn Nhật Bản Kômátsu Kiyôshi, chính khách và nhà văn hóa Thái Lan Pridi Phanomyông, “Ông hoàng Đỏ” người Lào Xuphanuvông...    Ở mảng đề tài thứ hai - một số sự kiện trong đời hoạt động cách mạng của Bác - tác giả đã sưu tầm được nhiều thông tin, tài liệu đáng tin cậy, chắp nối, liên kết lại với nhau, dựng lại một số sự kiện thú vị như quan hệ của Người với tổ chức hướng đạo sinh; về hai chuyến bí mật đi thăm Liên Xô trong những năm kháng chiến chống Pháp 1950 và 1952...  '),
(N'MS115', N'Thế Giới Sẽ Ra Sao?', N'Nhiều Tác Giả', N'NXB Dân Trí', N'Kiến thức bách khoa', 10, 129000, '/img/sach/the-gioi-se-ra-sao.jpg', N'Bạn đã sẵn sàng với chiếc xe đạp tự sửa chữa chưa?  Vậy còn viên thuốc mà uống xong bạn sẽ không bao giờ muộn phiền nữa thì sao?  Bạn muốn sống trong không gian hay trong một thế giới mà thuốc thang được điều chỉnh theo ADN của riêng bạn?  Có lẽ bạn sẽ dần quen với quyền lực ngày một lớn của trí tuệ nhân tạo.  Trong Thế Giới Rồi Sẽ Ra Sao? 18 nhà khoa học và chuyên gia sẽ khám phá những gì đang chuẩn bị xảy đến với nhân loại. Những chủ đề của họ trải dài từ những thứ trừu tượng (điện toán lượng tử và sinh học tổng hợp) tới những thứ cốt yếu (biến đổi khí hậu và năng lượng) tới những thứ thực sự thuộc về tương lai (du hành thời gian và dịch chuyển tức thời). Và cùng với đó, họ sẽ chỉ cho chúng ta thấy làm thế nào để tái cấu trúc xã hội sau tận thế, và một quả trứng rùa ốp lết sẽ dạy cho chúng ta những gì về việc cứu lấy hành tinh này.  Vật lý làm bạn bối rối? Bạn sẽ không bao giờ bối rối nữa sau khi đọc xong cuốn sách ngập tràn những hiện tượng, thí nghiệm lý thú này.'),
(N'MS116', N'Giáo Trình Xã Hội Học Đại Cương', N'Trương Thị Hiền', N'NXB Tổng Hợp TP.HCM', N'Giáo trình', 10, 90000, '/img/sach/giao-trinh-xa-hoi-hoc-dai-cuong.jpg', N'Trong chương trình đào tạo các ngành có trình độ đại học, học phần Xã Hội Học đại cương là học phần của khối kiến thức khoa học xã hội thuộc nội dung kiến thức giáo dục đại cương. Học phần này cung cấp cho người học những kiến thức cơ bản và có hệ thống về tri thức xã hội học, bao gồm: khái quát về khoa học xã hội học; phương pháp nghiên cứu xã hội học; một số chủ đề nghiên cứu của xã hội học như là: cơ cấu xã hội; hành động xã hội và tương tác xã hội; cá nhân và xã hội; bất bình đẳng và phân tầng xã hội; sự điều tiết của xã hội; chuyển biến xã hội,…   Việc tiếp cận xã hội học có thể hướng dẫn người học cách tổ chức suy nghĩ để đặt câu hỏi tốt hơn và hình thành câu trả lời tốt hơn khi quan tâm tới các hiện tượng xã hội. Đồng thời, việc học xã hội học cũng có thể giúp người học nhận thức rõ hơn rằng, sự sẵn lòng và khả năng nhìn thế giới từ quan điểm của người khác là điều quan trọng cho việc chung sống trong một thế giới ngày càng đa dạng và hội nhập.  Cuốn Giáo Trình Xã Hội Học Đại Cương này được biên soạn trước hết dành cho sinh viên đang theo học tại Trường Đại học Tây Nguyên nhưng chúng tôi hi vọng nó có thể là tài liệu tham khảo hữu ích cho các bạn sinh viên đang học tập, nghiên cứu xã hội học và những ai quan tâm tới bộ môn xã hội học.'),
(N'MS117', N'Đại Việt Sử Ký Tục Biên (1676 - 1789)', N'VNC Hàn Nôm', N'NXB Hồng Đức', N'Kiến thức bách khoa', 5, 300000, '/img/sach/dai-viet-su-ky-tuc-bien-1676-1789.jpg', N'Đại Việt Sử Ký Tục Biên do sử gia của triều đại Lê - Trịnh viết ra nên tất yếu đứng trên lập trường đề cao họ Trịnh, coi vua Lê ở hàng thứ yếu và coi nhà Tây Sơn cũng như nhà Nguyễn đều là kẻ thù. Lập trường này bộc lộ ở cách chọn lọc các sự kiện kịch sử và ở cả những ước lệ trong dùng chữ đặt câu. Nhưng đi sâu vào thì trên lập trường chung đó cũng có thể chia ra ba loại, với ba mức độ khác nhau: 1. Loại đề cao họ Trịnh: đó là các bản 2, 3, 4, 5, 6 và phần đầu của bản.   1. Tiếp tục lập trường đề cao họ Trịnh của phần Bản kỷ tục biên (tức các quyển XVII, XVIII, XIX) trong bộ Đại Việt Sử Ký Toàn Thư, các bản trên đây tuy chưa gọi các chúa Trịnh bằng những tiếng vẫn dùng để tôn xưng hoàng đế (những tiếng đó vẫn phải dành cho vua Lê) nhưng thái độ đề cao đã được bộc lộ không còn ở mức bình thường. Nhiều chỗ họ coi chúa Trịnh như những nhân vật “nhà trời” thần thông về năng lực, siêu việt về trí tuệ  2. Loại có sửa chữa bổ sung nhằm bày tỏ thái độ tôn trọng trong họ Nguyễn: đó là các bản chín và phần hai của bản một. Việc sửa chữa này biểu hiện ở hai phương diện: hoặc đưa vào sách một số sự kiện lịch sử của xứ Đàng Trong, hoặc dùng những mĩ hiệu tôn xứng hoàng đế để gọi các chúa Nguyễn. Nhưng dầu sao sự sửa chữa vẫn không quán triệt được trong toàn bộ bộ sách nên đây đó vẫn còn sót nhiều từ ngữ tỏ rõ lập trường đề cao họ Trịnh, miệt thị họ Nguyễn. Điều đó càng chứng tỏ những sự sửa chữa kia chỉ là sửa chữa vào một quyển sử đã viết từ trước, nói cách khác tuy tên gọi đã bị đổi đi (như Hậu Lê thì sự kỷ lược) nhưng văn bản của chúng về cơ bản vẫn là quyển Đại Việt Sử Ký Tục Biên viết dưới thời Lê - Trịnh.  3. Loại có sửa chữa bổ sung nhằm bày tỏ thái độ tôn trọng nhà Tây Sơn.'),
(N'MS118', N'Vĩ Đại Một Con Người', N'Trần Bạch Đằng', N'NXB Trẻ', N'Kiến thức bách khoa', 10, 52000, '/img/sach/vi-dai-mot-con-nguoi.jpg', N'Tập sách tập hợp những bài viết của Giáo sư Trần Văn Giàu và nhà báo Trần Bạch Đằng về một con người vĩ đại của dân tộc Việt Nam, một lãnh tụ vô sản mà tấm gương mãi ngời sáng - Hồ Chí Minh. Bằng văn phong sâu sắc, giản dị và những dẫn chứng hết sức sống động, các tác giả giúp chúng ta hiểu thêm về di sản đạo đức và tinh thần phong phú của Người; đặc biệt là sự liên hệ, đối chiếu tình hình biến chuyển mạnh mẽ của cách mạng vô sản theo các chiều hướng khác nhau trên phạm vi thế giới ngày nay, để khẳng định thêm tầm vóc những tiên tri, tiên lượng trong tư tưởng Hồ Chí Minh. Sách phù hợp với bạn đọc ở nhiều lứa tuổi, cũng có thể xem là một tài liệu nghiên cứu khoa học có giá trị'),
(N'MS119', N'99 Thử Thách Từ Vựng', N'Sarah Khan', N'NXB Lao Động', N'Kiến thức bách khoa', 10, 75000, '/img/sach/99-thu-thach-tu-vung-1.jpg', N'Không chỉ bởi hình thức đẹp, minh họa sinh động, 99 Thử Thách Từ Vựng còn nổi tiếng bởi cách truyền tải kiến thức gần gũi, dễ hiểu cũng như lượng kiến thức phong phú, cập nhật theo tiêu chuẩn chung của các nền giáo dục tiên tiến trên thế giới. Mình tin rằng đây sẽ là một lựa chọn tuyệt vời cho các em trong độ tuổi tiểu học với các câu đố vui kèm hình minh họa hết sức thú vị và khá hóc búa. Các em có thể gặp ở đây những kiến thức toán học quen thuộc, những câu đố mẹo đầy bất ngờ nhưng cũng có thể có những phần kiến thức có vẻ “quá sức” đối với em. Đừng lo lắng, đó chỉ là do sự khác biệt trong chương trình giáo dục giữa các quốc gia. Và em hãy coi như đây là một cơ hội để hiểu hơn về chương trình học ở một số nước khác trên thế giới, cũng là cơ hội để em khám phá thêm về khả năng tư duy của chính mình.'),
(N'MS12', N'Văn Phòng Không Nhạt Nhẽo', N'Hương Lan', N'NXB Dân Trí', N'Kỹ năng sống', 9, 80000, '/img/sach/van-phong-khong-nhat-nheo.jpg', N'Công việc văn phòng – chỉ nghe đến 4 từ này sẽ có rất nhiều người hình dung đó hình ảnh những nhân viên, mắt đeo kính, tay đánh máy, mồm ngáp dài trong căn phòng điều hòa mát…. Liệt kê như vậy cũng đã thấy nhàm chán. Ấy thế mà phải có điều gì mới khiến người ta “lao vào” công việc văn phòng như vậy chứ nhỉ. Thực tế, là có vô vàn điều thú vị, dở khóc dở cười đấy nhé.   Là một “con chiên ngoan đạo” suốt 12 năm, tác giả Hương Lan đã phải viết thành Văn Phòng Không Nhạt Nhẽo mới hết được những chuyện dở khóc dở cười như vậy.    Sẽ có rất nhiều nhầm tưởng được tháo gỡ: đi làm chỉ là sáng cắp cặp đi tối xách cặp về, quẩn quanh những công việc ngày nào cũng thế ngày nào cũng vậy,… thậm chí tác giả còn “kê đơn” cho những người đang chán việc.    Văn Phòng Không Nhạt Nhẽo dí dỏm, hài hước với các hình châm biết “không trượt phát nào với 1001 tình huống thường gặp trong văn phòng sẽ khiến bạn phải bật cười vì tin tôi đi, chắc chắn bạn sẽ được nhắc tên trong cuốn sách này đấy.    Sau cuối, tác giả mong muốn sẽ khiến bạn hạnh phúc với công việc mỗi ngày!'),
(N'MS120', N'Về Trung Quốc', N'Henry Kissinger', N'NXB Hội Nhà Văn', N'Kiến thức bách khoa', 5, 299000, '/img/sach/ve-trung-quoc.jpg', N'Henry Kissinger (1923) nguyên là Cố vấn An ninh Quốc gia và Ngoại trưởng Mỹ. Ông giữ vị trí trọng yếu trong quan hệ Mỹ - Trung dưới thời Tổng thống Nixon. Ông đã đề xuất chính sách Chính trị thực dụng (Realpolitik) để tái định hình quan hệ Mỹ - Trung, Mỹ - Xô và giải quyết các vấn đề nhức nối của thế giới, tạo nên xu thể hòa hoãn giữa các cường quốc trong thập niên 1970. Đặc biệt là trong chuyến thăm Trung Quốc của Nixon năm 1972 đã có tác động sâu sắc tới cục diện thế giới lúc đó và nhiều thập kỷ sau.  Kissinger đã xuất bản 14 cuốn sách và rất nhiều bài báo phản ánh quan điểm của ông về những nơi Mỹ muốn tạo ảnh hưởng, qua đó thể hiện nhiều bài học giá trị về hoạt động ngoại giao. Về Trung Quốc là cuốn sách thứ 13 của ông.  Về Trung Quốc xuất bản lần đầu năm 2011, khi Mỹ và Trung Quốc có những bước tiến mới trong chiến lược và kinh tế Mỹ - Trung. Cuốn sách này là một nỗ lực, mà một phần dựa trên các cuộc đối thoại với giới lãnh đạo Trung Hoa, nhằm giải thích cách suy nghĩ của người Trung Hoa về các vấn đề hòa bình, chiến tranh và trật tự quốc tế, cũng như mối quan hệ của lối tư duy ấy với cách tiếp cận mang tính thực dụng và cụ thể kiểu Mỹ. Trọng tâm chính của cuốn sách này là sự tương tác giữa giới lãnh đạo Trung Hoa và Mỹ kể từ khi Cộng hòa Nhân dân Trung Hoa được thành lập vào năm 1949.  Từ đó, Kissinger chỉ ra những khác biệt đã khiến Trung Quốc và Mỹ ở thế đối đầu trong hai thập kỷ và tái hiện cuộc chạm trán của Trung Quốc với những cường quốc khác. Tình thế đó chỉ thay đổi khi Mỹ và Trung Quốc tái lập quan hệ hữu nghị vào năm 1972 với vai trò kiến thiết quan trọng của Kissinger.  Tuy nhiên, ngay từ năm 2011, Kissinger cũng đã dự báo về những vấn đề lớn sẽ tác động tới quan hệ Mỹ - Trung, mà bây giờ đã thành hiện thực:  Thâm hụt thương mại của Mỹ trong quan hệ kinh tế với Trung Quốc  Không tương ứng trong các mối quan tâm chiến lược toàn cầu  Dựa trên kinh nghiệm lịch sử, cảnh báo về cuộc đối đầu định mệnh Mỹ - Trung, cho dù Trung Quốc chủ trương “trỗi dậy hòa'),
(N'MS121', N'Lược Sử Kinh Tế Học', N'Niall Kishtainy', N'NXB Thế Giới', N'Kiến thức bách khoa', 10, 135000, '/img/sach/luoc-su-kinh-te-hoc.jpg', N'Lược Sử Kinh Tế Học là cuốn sách nhập môn kinh tế học thú vị, dí dỏm dành cho mọi độc giả, từ sinh viên tới người đi làm, sách giải thích cho chúng ta một cách thấu đáo về đóng góp của các nhà kinh tế học vào sự hiểu biết thế giới của nhân loại.  Bạn chỉ còn đúng năm phút để tới kịp lễ khai mạc buổi hội thảo vô cùng quan trọng đối với sự nghiệp của mình trong khi vẫn đang loay hoay tìm chỗ đậu xe, và bạn chỉ có hai lựa chọn: chấp nhận bị phạt hành chính vì đỗ xe sai nơi quy định để tới hội thảo đúng giờ hay tiếp tục tìm kiếm bãi đỗ xe và đến muộn.  Đây chỉ là một ví dụ trong vô số bài toán kinh tế mà mỗi chúng ta phải giải hằng ngày. Như vậy, kinh tế học hóa ra không chỉ là một lĩnh vực tri thức cao siêu, xa vời dành riêng cho những nhà kinh tế học, chính khách hay doanh nhân, nó hiện diện trong mọi quyết định lớn nhỏ của đời sống. Nhận thức được tầm quan trọng của những hiểu biết kinh tế học căn bản đối với con người trong xã hội đương đại nhiều biến động khó lường, Niall Kishtainy đã trang bị cho độc giả đại chúng một “công cụ” sắc bén, hữu ích mà lại rất dễ dàng tiếp cận.  Lược sử kinh tế học chọn lọc và trình bày theo trật tự biên niên những hình thái, học thuyết, vấn đề và quy luật kinh tế then chốt trong các xã hội phương Tây suốt mấy ngàn năm qua với một góc nhìn khách quan, cách diễn giải cuốn hút và những ví dụ minh họa rất sinh động, gần gũi. Có lẽ, không ít độc giả, sau khi gấp cuốn sách này lại, sẽ không còn muốn chuyển kênh khi chương trình ti vi tường thuật một buổi tọa đàm của các chuyên gia kinh tế.'),
(N'MS122', N'Danh Nhân Quân Sự Việt Nam', N'Lê Minh Quốc', N'NXB Trẻ', N'Kiến thức bách khoa', 9, 125000, '/img/sach/danh-nhan-quan-su-viet-nam.jpg', N'Sách viết về 17 danh nhân quan sự Việt Nam: 1. Ngô Quyền, 2. Đinh Bộ Lĩnh, 3. Lê Hoàn, 4. Lý Thường Kiệt 5. Trần Quốc Tuấn, 6. Lê Lợi, 7. Nguyễn Huệ, 8. Trương Định 9. Võ Duy Dương,10. Nguyễn Trung Trưc, 11. Hoàng Hoa Thám, 12. Tôn Thất Thuyết, 13. Phan Đình Phùng,14. Nguyễn Duy Hiệu,15. Đinh Công Tráng, 16. Nguyễn Thiện Thuật và 17. Võ Nguyên Giáp. Danh Nhân Quân Sự Việt Nam là tập sách hay, có giá trị lịch sử và văn học, qua nghệ thuật viết theo lối kể chuyện với việc tổng hợp các dữ liệu công phu, hấp dẫn.'),
(N'MS123', N'Tương Lai Nhân Loại', N'Michio Kaku', N'NXB Thế Giới', N'Kiến thức bách khoa', 10, 179000, '/img/sach/tuong-lai-nhan-loai.jpg', N'Địa cầu hóa Sao Hỏa, viếng thăm các vì sao, sự bất tử và định mệnh bên ngoài Trái Đất  “Theo tôi, nếu không tiến ra không gian, nhân loại sẽ không có tương lai.” –Stephen Hawking  Vậy hành trình tiến ra không gian ấy sẽ thuận lợi hay khó khăn? Sẽ cần bao nhiêu thời gian và những công nghệ gì? Nhân loại đã chuẩn bị những gì cho định mệnh đó? Và cuộc sống con người khi ấy sẽ ra sao?  Hãy để người truyền thông cho khoa học Michio Kaku đưa bạn khám phá những “câu hỏi nóng” này của thời đại, bằng hiểu biết sâu sắc về vũ trụ, công nghệ sinh học, tên lửa hạt nhân… và bằng cách dẫn giải khó có thể khúc chiết, tường tận và thú vị hơn.  '),
(N'MS124', N'Ngoại Giao Của Chính Quyền Sài Gòn', N'Trần Nam Tiến', N'NXB Dân Trí', N'Kiến thức bách khoa', 10, 159000, '/img/sach/ngoai-giao-cua-chinh-quyen-sai-gon.jpg', N'Sau khi Hiệp định Genève được ký kết (21-7-1954), Hoa Kỳ quyết định gạt bỏ Pháp để trực tiếp can thiệp vào miền Nam Việt Nam, âm mưu tiến hành một cuộc chiến tranh chống sự thống nhất và toàn vẹn lãnh thổ của nhân dân Việt Nam. Để tạo cơ sở thuận lợi cho cuộc can thiệp này, Hoa Kỳ đã hậu thuẫn cho việc thành lập một chính phủ thân Mỹ ở miền Nam Việt Nam - chế độ Việt Nam Cộng hòa với Ngô Đình Diệm làm Tổng thống đầu tiên. Ngô Đình Diệm đã khéo léo tận dụng sự hỗ trợ của Hoa Kỳ để củng cố quyền lực, truất phế Quốc trưởng Bảo Đại và nắm toàn bộ quyền lực ở miền Nam Việt Nam.  Ngay sau khi thành lập, chế độ Việt Nam Cộng hòa đã tiến hành chống phá phong trào cách mạng ở miền Nam Việt Nam, biến miền Nam Việt Nam thành phòng tuyến chống phá cuộc cách mạng xã hội chủ nghĩa ở miền Bắc, ngăn chặn phong trào Cộng sản lan tràn ở khu vực châu Á nói chung và Đông Nam Á nói riêng. Chế độ Việt Nam Cộng hòa về cơ bản tuy “lệ thuộc” nặng nề vào Hoa Kỳ nhưng vẫn được nỗ lực tạo dựng hình ảnh một chủ thể “độc lập” trên trường quốc tế. Đặc biệt, trên lĩnh vực ngoại giao, chế độ Việt Nam Cộng hòa bên cạnh những chỉ đạo từ Hoa Kỳ, cũng đã có những chủ trương, đường lối ngoại giao riêng. Với mục tiêu tạo dựng vị thế “độc lập” cho chế độ Việt Nam Cộng hòa, chính quyền Ngô Đình Diệm đã tích cực mở rộng quan hệ với các quốc gia tư bản chủ nghĩa, các quốc gia thuộc Phong trào Không liên kết và Thế giới thứ ba, cũng như tham gia nhiều tổ chức quốc tế.'),
(N'MS125', N'Thượng Kinh Ký Sự', N'Lê Hữu Trác', N'NXB Hà Nội', N'Kiến thức bách khoa', 10, 69000, '/img/sach/thuong-kinh-ky-su.jpg', N'Lê Hữu Trác biệt hiệu là Hải Thượng Lãn Ông, sinh vào thời Lê mạt (1721) con quan Thượng thư huyện Đường Hào tỉnh Hải Dương nay thuộc Hải Hưng.  Không những là danh y, ông còn là một nhà văn, là ông tổ của nhà báo. Ông không chỉ truyền cho hậu thế bộ Y tông tâm lĩnh, Hải Thượng Lãn Ông Lê Hữu Trác còn để lại tập Thượng Kinh kí sự, viết vào thập niên 80 của thế kỷ XVIII (1781) một thời kỳ rối loạn của triều đình phong kiến Lê Trịnh – một giai đoạn khủng hoảng trầm trọng trước khi Quang Trung tiến quân ra Bắc Hà.  Với thiên ký sự trung thực và tài hoa này, tác giả đã kể lại cuộc hành trình từ Nghệ Tĩnh về Thăng Long của mình để chữa bệnh cho Chúa Trịnh khi được vời về phủ Chúa do tiếng tăm lừng lẫy của một đại danh y. Thượng Kinh Ký Sự kể lại thời gian sống ở Kinh thành biết bao biến động lớn, tả lại sự giao du với các công hầu khanh tướng thời Lê mạt và lúc nào tác giả cũng mong thoát khỏi vòng công danh phú quý về với núi cũ non xưa.'),
(N'MS126', N'Vừa Lười Vừa Bận Vẫn Giỏi Tiếng Anh', N'Nguyễn Văn Hiệp', N'NXB Phụ Nữ', N'Ngoại ngữ', 15, 168000, '/img/sach/vua-luoi-vua-ban-van-gioi-tieng-anhbia.jpg', N'Cuốn sách được trình bày theo 5 phần:   Phần 1: Phát âm cơ bản Phần 2: Những khái niệm căn bản cho người mới học tiếng anh Phần 3: Giao tiếp trong các tình huống hàng ngày Phần 4: Giao tiếp trong công viêc Phần 5: Cụm động từ và các danh từ  Cuốn sách hướng dẫn cho bạn cách học Tiếng Anh hiệu quả với bí kíp siêu tốc, phương pháp học Tiếng Anh không thể thiếu cho người bận rộn và ngại học. Nghe bất cứ khi nào có thể nghe, sáng trưa chiều tối. Nghe đi nghe lại mỗi bài Xem sách trước để làm rõ những chỗ chưa hiểu Nghe và nhắc lại những đoạn nói tiếng anh'),
(N'MS127', N'Kể Chuyện Bác Hồ', N'Khánh Linh', N'NXB Lao Động', N'Kiến thức bách khoa', 20, 50000, '/img/sach/tu-sach-bac-ho-ke-chuyen-bac-ho.jpg', N'Cuốn sách Tủ Sách Bác Hồ - Kể Chuyện Bác Hồ là một tác phẩm viết về Bác Hồ và cuộc đời đầy bôn ba sóng gió của người. Cuốn sách do Nhà sách Tân Việt và NXB Lao Động liên kết hợp tác phát hành.   Sách được xuất bản lần đầu năm 2017 và đã được công chúng nồng nhiệt đón nhận.  Cuốn sách đã trở nên thân thiết với các bạn đọc với nhiều lứa tuổi khác nhau, đặc biệt là các cháu thiếu niên.'),
(N'MS128', N'Vật Lý Của Tương Lai', N'Michio Kaku', N'NXB Thế Giới', N'Kiến thức bách khoa', 10, 199000, '/img/sach/vat-ly-cua-tuong-lai.jpg', N'“Vật lý của tương lai khiến người ta vừa bất ngờ vừa say mê lại vừa sợ hãi.” - The New York Times  Cuốn sách này đích thực là chuyến đi kỳ diệu xuyên suốt 100 năm tới của cuộc cách mạng khoa học ngoạn mục.  Dựa trên tư liệu phỏng vấn hơn 300 nhà khoa học hàng đầu thế giới – những người đang phát minh ra tương lai trong phòng thí nghiệm – và tính đến các nguyên tắc an toàn của phát triển công nghệ, nhà vật lý lý thuyết nổi tiếng Michio Kaku đã miêu tả tường tận cuộc cách mạng trong y học, máy tính, vật lý lượng tử và du hành vũ trụ đang diễn ra. Tất cả rồi đây sẽ mãi mãi thay đổi cuộc sống của chúng ta và toàn bộ tiến trình văn minh nhân loại.  Ai sẽ có việc làm? Những quốc gia nào sẽ thịnh vượng? Câu trả lời còn ở tương lai nhưng không quá xa tầm dự đoán.');
INSERT INTO sach (MaSach, TenSach, TenTG, NhaXB, TheLoai, SoLuong, GiaTien, ImageUrl, MieuTa) VALUES
(N'MS129', N'Cuộc Chiến Lỗ Đen', N'Leonard Susskind', N'NXB Trẻ', N'Kiến thức bách khoa', 9, 192000, '/img/sach/cuoc-chien-lo-den-bia.jpg', N'Cuộc chiến lỗ đen là sự ca tụng trí tuệ loài người và khả năng tuyệt vời của nó trong việc khám phá các định luật của tự nhiên. Đó là sự lý giải về một thế giới ở quá xa các giác quan của chúng ta, còn xa hơn cả cơ học lượng tử và Thuyết tương đối. Hấp dẫn lượng tử xem xét các đối tượng nhỏ hơn hàng trăm tỉ tỉ lần so với một prôton. Chúng ta chưa bao giờ từng trực tiếp tiếp xúc với những vật nhỏ đến như vậy và có thể sẽ không bao giờ, nhưng sự khéo léo của loài người sẽ cho phép chúng ta suy luận ra sự tồn tại của chúng, và thật đáng kinh ngạc, cổng vào thế giới đó lại chính là những vật thể với kích thước và khối lượng cực lớn: các lỗ đen. Cuộc chiến lỗ đen cũng là một cuốn biên niên sử về một khám phá. Nguyên lý toàn cảnh là một trong những khái niệm trừu tượng và phi trực giác nhất trong toàn bộ vật lý học. Đó là sự tích tụ của hơn hai thập kỷ đấu trí về số phận của thông tin khi bị rơi vào một lỗ đen. Đó không phải là một cuộc chiến tranh giữa các đối thủ hung hãn, mà thực sự ở đây tất cả những người tham chiến chủ yếu đều là bạn bè. Song đó là một cuộc chiến ác liệt về những ý tưởng giữa những người tôn trọng nhau một cách rất sâu sắc nhưng cũng bất đồng với nhau không kém phần sâu sắc.'),
(N'MS13', N'Đời Thay Đổi Khi Ta Thôi Đẩy', N'Sarah Knight', N'NXB Thế Giới', N'Kỹ năng sống', 9, 92000, '/img/sach/doi-thay-doi-khi-ta-thoi-day.jpg', N'Đừng sống cho người khác nhìn! Cũng đừng nhìn người khác mà sống.  Mỗi người đều có một bản đồ cho cuộc đời mình. Nếu bạn thấy mình có những dấu hiệu sau thì có lẽ bạn đang cầm nhầm bản đồ của người khác:  Ngại từ chối những lời mời vui chơi nhảy múa vì sợ bị loại khỏi nhóm  Mua sắm quần quật vì nể người quen bán hàng  Làm gì cũng phải nhìn người khác  Dễ so sánh bản thân với người xung quanh  Mỗi ngày chúng ta có 24 giờ, trừ 8 tiếng ngủ và trung bình khoảng 8-10 tiếng học tập hoặc làm việc ra thì chúng ta chỉ còn chưa tới 1/3 ngày để thực sự chú ý tới bản thân. Bạn có thể dành thời gian đó để chăm sóc mình hoặc lướt web trong vô thức, tùy bạn! Bạn có thể dành thời gian đó phát triển bản thân hoặc nghĩ xem người khác đang nghĩ gì về mình, tùy bạn! Bạn có thể dành thời gian đó để gọi điện về cho gia đình, mua quà cho bố mẹ hoặc chat chít những câu chuyện vô bổ tới tận khuya, tùy bạn!  Đời Thay Đổi Khi Ta Thôi Đẩy là lời cảnh báo và thức tỉnh cho những ai quá sa đà vào cuộc đời của người khác mà quên mất mình phải chăm cho chính cuộc đời mình.  Không phải ngẫu nhiên mà cuốn sách liên tục xếp hạng best-seller trên Amazon; tiêu thụ hơn 2 triệu bản; được xuất bản tại hơn 30 quốc gia; video chia sẻ về chủ đề cuốn sách của chính tác giả có gần 8 triệu view trên Youtube và chuẩn bị dựng thành phim. Vì Đời thay đổi khi ta thôi đẩy (The Life-Changing Magic of Not Giving a F*ck – tựa gốc) đã giúp hàng triệu người trên thế giới:  Sắp xếp lại tâm trí xem mình thực sự cần gì  Tiết lộ số thời gian khổng lồ mình lãng phí cho những điều vô bổ thay vì dùng nó để bản thân thấy có ích hơn  Mạnh dạn nói KHÔNG cho những điều không đáng  Biết cách thực sự cần phải làm gì để không cần quan tâm đến  Mọi người từ khắp nơi trên thế giới đã tiết lộ rằng cuốn sách thực sự thay đổi cuộc đời họ. Ai cũng nhận thức được rằng mình đã để ý quá nhiều, sống quá nhiều cho người khác nhưng không phải ai cũng biết cách để thoát ra được. Cuốn sách này sẽ giúp bạn.  Thời gian đã bắt đầu cạn dần kể từ khi ta cất tiếng khóc chào đời và không thể vay mượn thêm. Điều đó có nghĩa rằng bạn phải sống một cuộc đời thật ý nghĩa, có những trải nghiệm đáng giá và cuộc đời đó phải là của bạn.'),
(N'MS130', N'Khoa Học Cực Ngầu', N'Daniel Tatarsky', N'NXB Thế Giới', N'Kiến thức bách khoa', 10, 79000, '/img/sach/khoa-hoc-cuc-ngau.jpg', N'50 trò chơi khoa học khiến bạn ngã bổ chửng!  Cho ta thấy sự kỳ diệu của khoa học mỗi ngày!  Dành cho các nhà khoa học nghiệp dư rất ư tò mò!  Khoa học có thể rất bất ngờ, hấp dẫn và tất nhiên rất “ngầu”. Sách này giới thiệu 50 trò chơi minh chứng cho sự kỳ thú ấy. Và tuyệt nhất là bạn có thể thực hiện tất cả những trò này tại nhà một cách an toàn với sự hỗ trợ của người lớn. Mọi người sẽ rớt quai hàm xuống tận sàn nhà khi bạn giữ lon nước ngọt đứng nghiêng không đổ, khi bạn giật mạnh tấm khăn trải bàn mà bát đĩa vẫn ở nguyên trên bàn, khi bạn đút lọt quả trứng luộc vào chai cổ hẹp, hoặc khi bạn bẻ cong được ánh sáng xuyên qua dòng nước... Thử mà xem!  '),
(N'MS131', N'Cùng Con Lớn Lên Từng Ngày', N'Nguyễn Minh Hải', N'NXB Tổng Hợp TP.HCM', N'Tâm lý giáo dục', 10, 90000, '/img/sach/cung-con-lon-len-tung-ngay.jpg', N'Được trải nghiệm làm cha mẹ có lẽ là điều tuyệt vời nhất trong cuộc đời mỗi người. Làm cha mẹ là một bước trưởng thành thực sự, trở thành \"người lớn\", cơ bản vượt qua cái tuổi bồng bột, bởi khi làm cha mẹ thì thêm nhiều trách nhiệm, thêm gánh nặng ắt phải tự điều chỉnh bản thân để sống tốt với vai trò mới.  Làm cha mẹ thì bắt đầu hiểu được nỗi nhọc nhằn, vất vả để nuôi lớn một đứa con, từ đó biết yêu thương, hiếu đễ với những người sinh ra mình hơn. Làm cha mẹ tự nhiên sinh ra những cảm xúc mới lạ, những cảm giác yêu thương chưa từng có, cũng sự quấn quýt, quý mến, nhớ nhung, khao kháưng hoàn toàn khác với các tình cảm khác.  Làm cha mẹ nhìn con lớn lên mỗi ngày thì tự mình cũng lớn thêm, trưởng thành thêm, già dặn thêm trong lời nói, cách nghĩ, cách làm, trong sinh hoạt, làm việc.'),
(N'MS132', N'Không Hoảng Loạn Khi Con Kết Bạn', N'Michael Thompson', N'NXB Lao Động', N'Tâm lý giáo dục', 10, 99000, '/img/sach/khong-hoang-loan-khi-con-ket-ban.jpg', N'Không Hoảng Loạn Khi Con Kết Bạn nói về tầm quan trọng của đời sống xã hội đối với trẻ, khuynh hướng không thể tránh của trẻ như gây rắc rối và từ chối bạn bè đồng trang lứa, cũng như sức mạnh của tình bạn. Mỗi ngày, thầy cô và cha mẹ đều tận mắt chứng kiến các mối quan hệ của trẻ. Chúng ta nhìn thấy các con bị cô lập; nghe các con tán gẫu đầy vẻ ác ý; và thấy các con rất nhớ bạn bè. Thế là dù tươi đẹp hay sầu não thì những kí ức về các mối quan hệ từ thời thơ ấu của chính chúng ta bỗng dội về trong tâm trí. Tuy nhiên, không phải lúc nào những kí ức đó cũng là bảng chỉ dẫn phù hợp để chúng ta biết mình nên định hướng thế nào cho các con. Thực ra, những vết sẹo từ thời chúng ta học trung học thường khiến chúng ta lo lắng, do dự và ám ảnh tới chính các con mình trong quá trình trưởng thành. Thực tế là các con sẽ phải vừa học cách đối mặt và vừa thích nghi một cách tốt nhất. Rất ít người lớn hiểu rõ bản chất của việc: Trẻ nhỏ gây tổn thương cảm xúc cho nhau, hay trẻ chọc ghẹo và phản bội nhau. Vì trẻ “ích kỉ” ư? Tại sao chúng ta khó có thể ngăn cản trẻ cho nhau ra rìa? Tại sao một số trẻ lại khó kết bạn? Điều gì khiến một số trẻ được nhiều người yêu mến đến vậy? Tại sao một số trẻ chỉ hài lòng với một hoặc hai người bạn, chứ không quan tâm đến việc người khác có yêu mến mình hay không, trong khi một số trẻ lại coi đây là việc vô cùng quan trọng?'),
(N'MS133', N'Hãy Nói Với Con Rằng Con Giỏi Lắm', N'Nguyệt Minh', N'NXB Lao Động', N'Tâm lý giáo dục', 15, 55000, '/img/sach/hay-noi-voi-con-rang-con-gioi-lam.jpg', N'Hãy Nói Với Con Rằng Con Giỏi Lắm  là một trong những nhiệm vụ khó khăn nhất của các bậc cha mẹ. Làm cách nào để hướng trẻ phát triển thể chất và tinh thần một cách tích cực nhất? Đó cũng là điều cuốn sách Hãy Nói Với Con Rằng Con Giỏi Lắm muốn gửi tới các bậc làm cha mẹ.  Cuốn sách Hãy Nói Với Con Rằng Con Giỏi Lắm được chia làm 8 chương. Bằng những ví dụ minh họa xác thực và logic, văn phong giản dị và đầy tính thông tin. Cuốn sách thực sự là chìa khóa vàng mở cánh cửa tâm hồn trẻ.'),
(N'MS134', N'Con Là Của Báu', N'Tadashi Tsujii', N'NXB Lao Động', N'Tâm lý giáo dục', 15, 43000, '/img/sach/con-la-cua-bau.jpg', N'Con Là Của Báu như từng bài chia sẻ từ những thực tế nhưng chứa đựng nhiều bài học dạy con sâu sắc. Từng câu chuyện, từng hoàn cảnh quá gần với thực tế. Không những thế những trường hợp trong cuốn sách này rất phổ biến ở Việt Nam, và hầu như các ông bố bà mẹ nào cũng vấp phải. Nhưng không biết do đâu và phải làm cách nào để khắc phục. Và cuốn sách sẽ lý giải.  Con là của báu  Hãy nuôi con trong tâm trạng thoải mái  Ở một số gia đình kiểu mới (thời kỳ sau đại chiến thế giới lần thứ II), trong nhà, mấy cha con đều là độc giả thân thiết của tuần san truyện tranh Shounen Jump. Người cha thường hay dẫn con đến các trung tâm trò chơi điện tử, cho con chơi trò đập thú, còn mình thì say mê với các trò chơi yêu thích. Kết quả là có những trường hợp đứa trẻ gặp phải những tai nạn đáng tiếc khi cha đang mải mê với trò pachinko Ngược lại, nhìn cảnh hai cha con mặc quần áo đôi, dắt tay nhau đi bộ trên phố, tôi lại có cảm giác người cha này đang nuôi con với một tâm trạng thật nhẹ nhàng và thoải mái.  Thời đại trẻ em lên ngôi  Thật đáng buồn khi tự chúng ta làm khổ mình khi nuôi dạy con cái. Hiện nay, ở Nhật, các bậc phụ huynh cho rằng cứ theo ý mình và kinh nghiệm bản thân cũng có thể nuôi dạy con đâu ra đấy, trong khi điều này gần như là không thể.  Trẻ được voi đòi tiên  Chúng không biết trân trọng mọi thứ, nói cách khác là không thể thay đổi quan điểm cố hữu của mình để hòa đồng với môi trường xung quanh. Nhiều trẻ hiện nay nhận thức một cách rất phiến diện: khi chơi nếu mình là nhân vật chính thì sẽ chơi rất sôi nổi, hoạt bát; còn nếu rời khỏi vai chính thì ngay lập tức sẽ không chơi nữa.'),
(N'MS135', N'Cha Mẹ Pháp Không Đầu Hàng', N'Pamela Druckerman', N'NXB Lao Động', N'Tâm lý giáo dục', 15, 45000, '/img/sach/cha-me-phap-khong-dau-hang.jpg', N'Cha Mẹ Pháp Không Đầu Hàng là 100 bí quyết quan trọng nhất mà tác giả đã học được từ các cha mẹ và các chuyên gia giáo dục trẻ tại Pháp. Người đọc, không nhất thiết phải sống giữa lòng Paris mới có thể áp dụng những bí quyết này. Thậm chí cha mẹ cũng chẳng cần phải thích phô mai mới dạy được con cách yêu phô mai của người Pháp. Tuy vậy, tôi khuyên bạn nên ngó qua những thực đơn ở phần cuối cuốn sách. Đó là thực đơn mẫu mà trẻ em ở các nhà trẻ tại Paris được ăn, hơn nữa những món đó không phải dành riêng cho trẻ nhỏ mà còn là những món ngon cho cả người lớn nữa.   Tác giả tin tưởng vào cả 100 bí quyết trên. Nhưng đây không phải là những bí quyết do tác giả tạo ra, hay tự nhận là của mình. Và không phải tất cả những bí quyết này đúng với tất cả mọi nhà. Người Pháp quan niệm rất rõ ràng rằng mỗi đứa trẻ là một cá thể độc lập, và không tồn tại bất cứ một công thức chung nào khi nuôi dạy trẻ. Khi đọc cuốn sách này, bạn sẽ nhận thấy đằng sau những bí quyết đơn lẻ luôn là một số những nguyên tắc chung. Một trong những nguyên tắc nền tảng trụ cột với tôi, với tư cách một người Mỹ, đó là: Nếu toàn bộ cuộc sống sinh hoạt của gia đình chỉ xoay quanh những đứa trẻ, điều đó không có lợi cho bất cứ ai, kể cả cho chính đứa trẻ trung tâm đó!   Một số kiến thức giáo dục của Pháp thực sự sáng suốt và tinh túy. Cha mẹ Pháp đồng loạt tin tưởng rằng trẻ sơ sinh cũng là các cá thể biết suy nghĩ và khi nuôi dạy con cha mẹ cần kết hợp một chút nghiêm khắc với rất nhiều tự do, rằng cha mẹ cần lắng nghe con thật kỹ nhưng không có nghĩa là sẽ phụng sự y như những gì con muốn. Cách cha mẹ Pháp chuyển cho con từ ăn dặm sang ăn đồ ăn như người lớn thực sự đang kinh ngạc. Quan trọng hơn cả, họ tuyệt đối tin tưởng rằng trí khôn và sự nuôi dạy con sáng suốt nhất chỉ đến khi cha mẹ bình tĩnh và thư thái. Cái hay là ở chỗ, ở nước Pháp, họ có cả một dân tộc với tất thảy các bậc cha mẹ đều đồng thời áp dụng phương châm này, như một nhóm khổng lồ và thống nhất của các bậc cha mẹ cùng chí hướng. Hãy đến đây mà xem. Bạn sẽ thấy kinh ngạc.'),
(N'MS136', N'Vì Chơi Là Học - Dành Cho Trẻ', N'Tadashi Tsujii', N'NXB Lao Động', N'Tâm lý giáo dục', 15, 43000, '/img/sach/vi-choi-la-hoc-danh-cho-tre-1-2-tuoi.jpg', N'Nhiều bậc cha mẹ cho rằng việc chơi với con là một bản năng, chỉ cần yêu thương con, chơi với con là chuyện đương nhiên, không học cũng biết và cũng giỏi. Liệu điều đó có đúng ?  Ai cũng thích chơi nhưng không phải ai cũng biết cách chơi thật hay, thật “chuẩn và chất”. Việc chơi với trẻ cũng như vậy, nhưng còn có phần thách thức hơn bởi đối với trẻ, chơi không chỉ là chơi, chơi chính là học, và là chương trình học toàn thời gian có tính chất quyết định trong cuộc đời đứa trẻ. Khoảng thời gian chơi đùa cùng cha mẹ góp phần rất lớn đến quá trình hình thành nhận thức, tính cách và kỹ năng của trẻ sau này, mà toàn bộ quá trình này đều được lên ý tưởng và đạo diễn bởi chính cha mẹ trẻ.  Thêm nữa, những ngày tháng đầu đời của trẻ cũng là lúc cả gia đình bắt đầu tìm hiểu nhau và xây dựng mối gắn kết tình cảm. Thông qua các trò chơi, nếu để tâm quan sát và mở lòng đón nhận sự khác biệt của trẻ, cha mẹ có thể khám phá ra nhiều điều để hiểu con mình hơn.  Cuốn thứ hai trong bộ sách Vì Chơi Là Học - Dành Cho Trẻ 1 - 2 Tuổi của tác giả Tadashi Tsujii đã gợi ý cho các bậc cha mẹ có con nhỏ từ 1-2 tuổi cách chơi cùng con và những trò chơi nhẹ nhàng, thú vị nhưng mang tính giáo dục cao để cha mẹ có thể hướng dẫn con tự chơi, tự khám phá và hoàn thiện các kỹ năng.  Khi kết thúc giai đoạn sơ sinh và bước vào giai đoạn tập đi, trẻ có rất nhiều thay đổi trong nhận thức và “ý thức cá nhân”, bởi vậy, cách cha mẹ chơi cùng con và hướng dẫn con chơi cũng cần thay đổi rất nhiều. Vẫn với cách viết nhẹ nhàng, những miêu tả chi tiết kết hợp với hình ảnh minh hoạ sinh động, tác giả đưa ra những “bài tập” chơi phù hợp với từng tháng trong suốt năm tuổi thứ hai của trẻ.  Giai đoạn trẻ được 1 năm 1 tháng tuổi, việc rèn luyện các kỹ năng tương tác xã hội của trẻ nên được chú trọng. Bằng những trò chơi đơn giản như để bé thấy sự thay đổi biểu cảm hoặc bắt chước hành động hay cách trò chuyện của mẹ, bé sẽ học được cách giao tiếp, ứng xử và phát triển nhận thức về các mối quan hệ. Ngoài ra, mẹ có thể hướng dẫn bé chơi cùng những món đồ chơi yêu thích để rèn luyện sự khéo léo và kỹ năng tổ chức.  Giai đoạn trẻ được 1 năm 2 tháng tuổi, việc giáo dục trẻ các quy tắc xã hội và ý thức tự lập được đề cao. Trẻ ở giai đoạn này rất thích thú với việc tập đi, khám phá thế giới và tự làm các công việc cá nhân. Cha mẹ nên tạo cho con một môi trường an toàn, thoải mái và hướng dẫn con tự chơi những trò chơi kích thích trí tò mò và rèn luyện khả năng vận động cũng như sự khéo léo của đôi tay.'),
(N'MS137', N'Trí Thông Minh Đa Dạng Của Trẻ', N'Yoon OK In', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 129000, '/img/sach/tri-thong-minh-da-dang-cua-tre-1280.jpg', N'Khi nói tới trí thông minh, đại đa số chúng ta đều nghĩ tới chỉ số IQ hay chỉ số biểu hiện trí thông minh cảm xúc EQ. Nhiều năm qua, người ta sử dụng bài test IQ để đánh giá trí thông minh của một người tuy nhiên bài kiểm tra này không hoàn hảo bởi vì nó chỉ chú trọng tới ngôn ngữ hoặc logic toán học mà chưa chú ý tới khả năng tương tác, cảm xúc, nghệ thuật hay kỹ năng xã hội của người đó, vì vậy chỉ số này không đánh giá đầy đủ về năng lực phong phú của con người.  Bắt đầu từ học thuyết Trí thông minh đa dạng của Giáo sư Howard Gardner – trường Đại học Harvard, sau nhiều năm nghiên cứu và trong giảng dạy, tác giả Yoon Ok In đã viết lên một tài liệu vô cùng hữu ích có tên là “Trí thông minh đa dạng của trẻ”.  Trong Trí Thông Minh Đa Dạng Của Trẻ tác giả Yoon Ok In đã chỉ ra rằng từ khi sinh ra mỗi đứa trẻ đều sở hữu 8 trí thông minh, một vài trí thông minh nổi trội hơn và một vài trí thông minh kém hơn. Đó là:  Trí thông minh ngôn ngữ Trí thông minh Logic – Toán học Trí thông minh nội tâm Trí thông minh tương tác giao tiếp Trí thông minh âm nhạc Trí thông minh không gian Trí thông minh thiên nhiên'),
(N'MS138', N'4 Món Quà Tặng Con', N'Barbara Coloroso', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 109000, '/img/sach/4-mon-qua-tang-con.jpg', N'Những mối bận tâm của các bậc cha mẹ luôn là:  Làm sao để nuôi dạy trẻ thành những người kiên định, có trách nhiệm và lòng trắc ẩn? Làm sao để nuôi dạy trẻ thành những người phụ thuộc vào việc được khen ngợi và  tặng thưởng? Làm sao để giúp trẻ học được cách quan tâm sâu sắc, chia sẻ hào phóng và sẵn lòng giúp đỡ? Làm sao để giữ chúng không bị vấy bẩn bởi thói đê tiện xung quanh? Làm sao để dạy bọn trẻ tự đứng lên bảo vệ bản thân, hiểu được quyền lợi của chúng trong khi biết tôn trọng quyền và nhu cầu hợp pháp của người khác? Làm sao để chúng đứng lên chống lại bất công, làm điều đúng đắn khi gánh nặng đè lên vai?'),
(N'MS139', N'Cùng Con Bước Qua Tiểu Học', N'Laura Numeroff', N'NXB Văn Học', N'Tâm lý giáo dục', 9, 89000, '/img/sach/cung-con-buoc-qua-tieu-hoc.jpg', N'Sống là để vui cười, những điều khác đều không quan trọng  Cùng con trưởng thành, tâm hồn chúng ta cũng thêm phong phú.   Tiểu học là khởi điểm quan trọng để trẻ trưởng thành, những năm này quyết định và ảnh hưởng đến mấy mươi năm phía sau của đời người.   Đây là chia sẻ chân thành của một người mẹ bình thường mà ưu tú. Chị dùng ngòi bút giản dị mà tinh tế của mình, hồi tưởng lại từng dấu chân trưởng thành của con trong những năm tiểu học. Từ “thỏi nam châm” làm con yêu thích học tập, “hòm thư gia đình” làm tăng sự thấu hiểu, “nguyên tắc gia đình” bồi dưỡng ý thức trách nhiệm đến những sách báo mà mẹ “tùy ý” để lung tung, màn chào đón về nhà mà cha “đặc biệt” thiết kế,… trang sách nào cũng đầy ắp trí tuệ giáo dục độc đáo mà hiệu quả, công phu mà không để lại dấu vết.   Sự trưởng thành của trẻ là tự nhiên và đặc thù, cha mẹ cần học cách biến tình yêu sâu lắng thành sự đợi chờ nhẫn nại mà ấm áp, hạ thấp kì vọng, bước chậm lại, đồng hành cùng con bước qua những năm tiểu học không hối hận.'),
(N'MS14', N'Điều Vĩ Đại Đời Thường', 'Robin Sharma', N'NXB Trẻ', N'Kỹ năng sống', 10, 73000, '/img/sach/dieu-vi-dai-doi-thuong.jpg', N'Hành trình trở nên vĩ đại khởi đầu từ những điều thật giản dị và gần gũi, cũng giống như hành trình vạn dặm khởi đầu từ một bước chân giản đơn. 101 câu chuyện và ý tưởng của tác giả trong quyển sách này chính là 101 bài học minh chứng cho triết lý đơn sơ ấy.  Không có cuộc sống nào là hoàn hảo, tất cả chúng ta phải đối mặt với nhiều thử thách, từ đơn giản đến nghiêm trọng… Nhưng chúng ta đều có quyền lựa chọn vượt lên mọi nghịch cảnh để vươn tới vị trí cao nhất và tốt nhất của cuộc sống.'),
(N'MS140', N'Trong Sách Có Gì Mà Vui Thế?', N'Mem Fox', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 89000, '/img/sach/trong-sach-co-gi-ma-vui-the.jpg', N' “Cô ấy kể cho tôi về cậu con trai hồi chín tuổi đã từng không thích đọc sách. Khả năng đọc không tốt đã khiến cậu bé chản nản, bố mẹ cũng vô cùng phiền lòng. Cậu sắp sửa bị xếp vào một lớp học đặc biệt trong trường. Sau khi đọc cuốn “Trong sách có gì mà vui thế?” của tôi, cô ấy biết những gợi ý trong sách chính là câu trả lời cho những lời cầu nguyện của cô lâu nay.” – Mem Fox  Trong Sách Có Gì Mà Vui Thế? là một phép màu giúp các thành viên trong gia đình gắn bó hơn. Mỗi ngày họ ngồi xuống cùng nhau vui vẻ đọc sách, họ hiểu ra đọc sách cùng con vui đến chừng nào, họ cũng không còn xem việc đọc như một trọng trách nặng nề mà đó là khoảng thời gian vui vẻ bên nhau mỗi ngày mà ai trong chúng ta cũng đều háo hức mong chờ.  Tác giả Mem Fox đã mang đến những chia sẻ thực tế về việc đọc sách cho trẻ, đặc biệt là tầm quan trọng của việc đọc to – một phương pháp hữu ích giúp trẻ học đọc một cách dễ dàng. Với cơ sở lý thuyết khoa học dễ hiểu và đáng tin cậy, các ví dụ minh họa thực tế và thuyết phục, Mem Fox đã chứng minh cho chúng ta thấy hiệu quả thần kỳ của việc cha mẹ đọc to thành tiếng cho con nghe, dù chỉ vài phút mỗi ngày.  “Chỉ với những chương sách ngắn gọn nhưng đầy hấp dẫn, Mem Fox đã cho chúng ta thấy được niềm vui vô bờ khi đọc sách cùng con trẻ” – The Toronto Star.  “Không cần đao to búa lớn, những chỉ dẫn nhẹ nhàng và đơn giản của Fox đã đi thẳng vào trái tim của tất cả các phụ huynh chính nhờ sự thông thái và tình yêu thương lớn lao của bà đối với trẻ em” – The New York Times Book Review.'),
(N'MS141', N'Cha Mẹ Vô Điều Kiện', N'Nguyễn Thị Thu Hiền', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 129000, '/img/sach/cha-me-vo-dieu-kien.jpg', N'Cha Mẹ Vô Điều Kiện là cuốn sách sẽ khiến cha mẹ có cái nhìn hoàn toàn khác về tình yêu thương dành cho con trẻ.   Thực tế, chúng ta có xu hướng yêu quý những đứa trẻ ngoan ngoãn, biết vâng lời hơn là những đứa trẻ nghịch ngợm. Cũng giống như việc chúng ta thường hay thỏa thuận với trẻ rằng nếu con không ngồi yên mẹ sẽ không cho con đi siêu thị nữa, nếu con không trật tự thì con sẽ không được ăn tối… Tất cả những điều đó đã vô tình khiến chúng ta đã đặt điều kiện cho thứ tình yêu vốn thiêng liêng và thuần khiết nhất.   Phụ huynh nào cũng từng dùng hình phạt để dạy bảo con, nhưng đã bao giờ họ tự hỏi việc dùng hình phạt có giúp trẻ tiến bộ và hiệu quả lâu dài hay không? Và nếu việc dùng hình phạt có hiệu quả thật thì tại sao cha mẹ phải dùng đi dùng lại nó mãi, thậm chí với cấp độ phạt nặng hơn?   Tương tự như hình phạt thì dùng phần thưởng để khuyến khích trẻ sẽ khiến trẻ bị mất đi động lực nội sinh. Khi làm việc, nếu phần thưởng càng nhiều người ta càng dễ mất đi sự thích thú và đam mê trong công việc vì họ mải làm để nhận phần thưởng.   Do đó dùng phần thưởng hay hình phạt để khuyến khích và khuyên răn con không còn là cách nuôi dạy hiệu quả nữa. Cuốn sách này cũng chỉ ra cho cha mẹ thấy rằng việc dùng hình phạt sẽ gây ra những hậu quả như thế nào, cha mẹ có nên kiểm soát quá mức và đặt ra những áp lực thành tích cho trẻ hay không. Và đâu mới là cách tốt nhất để thể hiện tình yêu thương vô điều kiện.   Trân trọng giới thiệu tới độc giả Cha Mẹ Vô Điều Kiện của Alfie Kohn, tác giả của 14 cuốn sách và rất nhiều bài báo về hành vi của con người, chủ đề giáo dục và phương pháp nuôi dạy trẻ. Các cuốn sách của ông được dịch ra nhiều ngôn ngữ như Nhật Bản, Hàn Quốc, Tây Ban Nha, Đức, Italy, Nga, Trung Quốc…'),
(N'MS142', N'Con Chúng Ta Không Sao Đâu', N'Cheonseok Suh', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 129000, '/img/sach/con-chung-ta-khong-sao-dau.jpg', N'Sẽ ra sao khi những người mẹ đôi khi quát mắng và đối xử nhẫn tâm với trẻ? Những đứa trẻ phát triển chậm, những đứa trẻ kén ăn, những đứa trẻ rụt rè, những đứa trẻ không thể hòa nhập với bạn bè…. sẽ lớn lên như thế nào? Làm thế nào khi vợ chồng xung đột về quan điểm giáo dục con cái ?   Con Chúng Ta Không Sao Đâu sẽ giải đáp tận tình, chi tiết và xoa dịu những nỗi lo lắng, bất an mà cha mẹ nào cũng gặp phải trong giáo dục con cái . bởi tác giả cuốn sách chính là bác sĩ chuyên khoa tâm lý trẻ em rất nổi tiếng ở Hàn Quốc. Theo ông, không có bậc cha mẹ hoàn hảo, cũng không có đứa trẻ hoàn hảo. Cha mẹ hãy nhìn vào những ưu điểm của con để lắng nghe thấu hiểu, những vấn đề phát sinh của con chỉ là tạm thời, vì vậy hãy thoải mái lạc quan lên. Tất cả rồi sẽ ổn, con chúng ta hoàn toàn không sao đâu!'),
(N'MS143', N'Cùng Con Học Nói', N'Sally Ward', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 9, 129000, '/img/sach/cung-con-hoc-noi.jpg', N'Cùng Con Học Nói là cuốn sách được đúc kết sau nhiều năm nghiên cứu và thực nghiệm Chương trình Trẻ học nói của Tiến sĩ Sally Ward. Theo chuyên gia, ba năm đầu đời là giai đoạn then chốt đối với sự phát triển ngôn ngữ và khả năng giao tiếp, cũng như khả năng lắng nghe và tập trung sự chú ý của trẻ. Giúp trẻ phát triển khả năng giao tiếp, lắng nghe và thấu hiểu chính là tạo nền tảng cho khả năng học tập của trẻ trong tương lai. Với 30 phút mỗi ngày, cha mẹ hoàn toàn có thể tạo ra sự khác biệt cho cuộc đời của trẻ.  Thông qua Chương trình Trẻ học nói, cha mẹ sẽ:  - Tạo Ra một môi trường có lợi cho sự phát triển của trẻ ngay tại nhà. - Nuôi Dưỡng trẻ trở thành một chủ thể giao tiếp tự tin. - Tăng Cường khả năng tập trung và ghi nhớ thông tin của trẻ. - Kích Thích trẻ bằng các loại đồ chơi và các cuốn sách theo từng giai đoạn. - Nhận Diện các vấn đề có thể cản trở sự phát triển ngôn ngữ của trẻ. - Vận Dụng các trò chơi, ý tưởng và ngôn ngữ để làm giàu trí tưởng tượng của trẻ.  Cùng Con Học Nói là sự kết hợp nhuần nhuyễn kiến thức sinh lý và tâm lý nhi khoa. Trọng tâm của cuốn sách là Chương trình trẻ học nói với chìa khóa thành công là 30 phút mỗi ngày, cha/mẹ chơi với con trong môi trường yên tĩnh không bị gián đoạn hay quấy rầy. Trong khoảng thời gian đó, cha mẹ và con trò chuyện với nhau theo phương pháp mà cuốn sách đưa ra, hiệu quả sẽ phát huy nhanh chóng.  Cùng Con Học Nói này là nguồn tài liệu đủ tính lý thuyết, cung cấp nền tảng khoa học đồng thời có những gợi ý bài tập thực hành cụ thể cho độc giả. Đây là nguồn tham khảo tốt cho các tổ chức nghiên cứu, đơn vị trị liệu đồng thời cũng là cuốn sách gối đầu giường cho các bậc cha mẹ, những người có thể ít có điều kiện nghiên cứu học thuật nhưng có thời gian và hiểu biết rõ nhất về hoàn cảnh cũng như tính cách của con mình.'),
(N'MS144', N'Học Làm Cha Mẹ Hiệu Quả', N'Thomas Gordon', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 109000, '/img/sach/hoc-lam-cha-me-hieu-qua.jpg', '  Một đứa trẻ muốn bơi, nó phải mất công tập luyện nhiều tháng trời. Một người trưởng thành muốn tập lái ô tô họ cũng phải học lý thuyết và thực hành nhiều buổi. Thế nhưng mỗi năm có hàng triệu đàn ông và phụ nữ nhận nhiệm vụ cực kỳ khó khăn và mất nhiều công sức là làm cha mẹ – chịu trách nhiệm hoàn toàn về tâm sinh lý của đứa bé sơ sinh và nuôi dạy nó thành người – có bao nhiêu người được đào tạo cho công việc đó?   Gần gũi và kinh điển, Học Làm Cha Mẹ Hiệu Qủa là cuốn sách tiên phong dạy cha mẹ làm thế nào để giao tiếp hiệu quả hơn với con cái và cung cấp lời khuyên từng bước một giải quyết mâu thuẫn gia đình mà tất cả mọi người đều chiến thắng. Một cuốn sách bổ ích cho tất cả những người đang và sắp bước vào hành trình làm cha mẹ bởi nó sẽ chỉ cho bạn:  –  Làm thế nào để tránh trở thành cha mẹ dễ dãi?  Làm thế nào để lắng nghe trẻ nói chuyện với bạn và nói như thế nào để trẻ lắng nghe bạn? Làm thế nào để dạy trẻ “sở hữu” những vấn đề của chúng và tự giải quyết chúng? Làm thế nào để sử dụng phương pháp “Không Thua Cuộc” để giải quyết xung đột?'),
(N'MS145', N'Dạy Con Tập Bơi', 'Usborne', N'NXB Lao Động', N'Tâm lý giáo dục', 10, 79000, '/img/sach/day-con-tap-boi.jpg', N'Dạy Con Tập Bơi cung cấp kiến thức giúp bạn và con bạn biết cách phòng tránh các tai nạn do đuối nước. Cuốn sách cũng giúp bạn cùng con làm quen với nước và học các kĩ thuật bơi cơ bản mà không tốn thời gian, tiền bạc lại an toàn.  Cuốn sách được viết bởi hai tác giả là chuyên gia bơi lội và huấn luyện viên bơi lội của Vương quốc Anh.  Dạy Con Tập Bơi được hiệu đính và giới thiệu bởi Tiến sĩ Phạm Anh Tuấn, giám đốc Trung tâm E-boi - người có hơn 9 năm kinh nghiệm trong việc dạy bơi cho trẻ. Cuốn sách được Tủ sách Người Mẹ Tốt - thương hiệu hàng đầu về sách giáo dục gia đình ở Việt Nam dịch từ ấn phẩm “Teach Your Child To Swim” của USBORNE - nhà xuất bản danh tiếng hàng đầu của Vương quốc Anh.'),
(N'MS146', N'Giúp Con Phát Triển Ngôn Ngữ', N'Kato Kumiko', N'NXB Văn Học', N'Tâm lý giáo dục', 10, 69000, '/img/sach/giup-con-phat-trien-ngon-ngu.jpg', N'"Nếu yêu thương con thực sự, thì trước khi phó mặc con cho “những-người-ngoài-có-vẻ-tốt-bụng”, “những-người-ngoài-có-vẻ-đáng-tin-cậy”, chẳng phải trước tiên cha mẹ nên cố gắng hết sức để làm cho con những điều mà mình có thể làm hay sao?...Và điều tiên quyết: cha mẹ hãy trở thành những người mà con có thể gửi gắm niềm tin, hãy xây dựng mối quan hệ tin cậy với con. Bởi vì, tôi tin tưởng chắc chắn, điều này liên quan mật thiết đến việc nuôi dưỡng con thành một đứa trẻ thông minh, một đứa trẻ biết nghĩ cho người khác, hơn bất cứ trwingf học tốt nào, hơn bất cứ tài liệu giáo dục tuyệt vời nào khác…  Phương pháp “dạy con bằng danh từ vốn có” trong cuốn sách này, thực ra chỉ là một cách nuôi dạy con cụ thể để cha mẹ vừa có thể xây dựng mối quan hệ tin tưởng với con, vừa đồng hành cùng con lớn lên mà thôi… Tất cả những nội dung của Giúp Con Phát Triển Ngôn Ngữ này đều là những câu chuyện có thật. Vì thế dù hay dù dở, nó cũng là một cuốn sách “tham khảo” dành cho các bậc cha mẹ trên con đường dựng xây mối quan hệ với con mình.\"'),
(N'MS147', N'Bí Quyết Giúp Con Giỏi Tiếng Anh', N'Claire Selby', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 49000, '/img/sach/bi-quyet-giup-con-gioi-tieng-anh.jpg', N'Bí Quyết Giúp Con Giỏi Tiếng Anh là lời giải đáp cho những câu hỏi mà mọi phụ huynh đều trăn trở trong quá trình cùng con học tiếng Anh: Why – Tại sao lại là tiếng Anh; When – Khi nào thì con bạn nên bắt đầu học tiếng Anh; How – Làm thế nào để trẻ học tiếng Anh hiệu quả nhất; What – Bạn nên kì vọng con học được những gì ở mỗi độ tuổi; Who – Ai là người nên giúp con bạn học tiếng Anh?   Kết cấu cuốn sách gồm 8 phần, 5 phần đầu để giải quyết 5 câu hỏi cơ bản trên, những phần còn lại là lời gợi ý về cách các bậc cha mẹ có thể giúp trẻ học tiếng Anh. Những chia sẻ của tác giả Claire Selby – nhà sáng lập tổ chức giáo dục Yellow House chuyên sâu trong việc cung cấp sách, tài liệu và chương trình học tiếng Anh cho trẻ, sẽ khiến không chỉ các bậc cha mẹ mà ngay cả các chuyên gia kinh ngạc. Một điều thú vị của cuốn sách là cha mẹ có thể đọc từ đầu, hoặc đọc từ bất cứ phần nào mình quan tâm nhất.   Kết thúc cuốn sách, tác giả Claire Selby nhắn nhủ tới các bậc cha mẹ “Bây giờ các bạn có thể thôi lo lắng và hãy bước những bước đầu tiên cùng con bạn vào một cuộc đời tiếp cận dễ dàng với tiếng Anh” vì “ngày nay, tiếng Anh là một trong những món quà giáo dục quan trọng nhất mà bạn có thể trao cho một đứa trẻ”.'),
(N'MS148', N'Cha Mẹ Thời Đại Kĩ Thuật Số', N'Shin Yee Jin', N'NXB Văn Học', N'Tâm lý giáo dục', 10, 109000, '/img/sach/cha-me-thoi-dai-ki-thuat-so.jpg', N'Nuôi con thành kẻ thông minh sáo rỗng chỉ có kiến thức mà không có trí tuệ, không thể làm chủ cơn nóng giận và cảm giác bất an, không thể hòa đồng với bạn bè, chỉ biết tìm đến mẹ, không biết phải xoay sở ra sao khi chỉ có một mình,…hay để chúng trở thành đứa trẻ trưởng thành có trí tuệ và “trưởng thành thực sự”?  Tại sao ngày càng xuất hiện nhiều trẻ em “trưởng thành giả” trong thế giới kĩ thuật số?  Tại sao những thiên tài của thung lũng Silicon lại cách li máy vi tính ra khỏi cuộc sống thường ngày của con cái họ?  Tại sao họ lại gửi con mình vào những ngôi trường không có máy vi tính?  Pháp cấm sử dụng điện thoại di động trong trường học  Đức và Phần Lan đưa ra khuyến cáo mạnh mẽ về việc hạn chế sử dụng điện thoại di động ở trẻ em…  Ngày nay, các bậc cha mẹ tại các quốc gia có nền giáo dục phát triển đều quan tâm đến “sự phản tác dụng của các thiết bị công nghệ kĩ thuật số” và đang tìm kiếm giải pháp trong cách giáo dục con cái thời đại kĩ thuật số.  Nếu bạn là cha mẹ sống trong một đất nước phát triển về công nghệ thông tin, nơi mà các thiết bị công nghệ kĩ thuật số đang nhận được sự hưởng ứng nhiệt liệt trong vai trò là công cụ học tập ưu việt, trợ tá nuôi dạy con đắc lực, thứ đồ chơi vạn năng, thì đây là phương pháp nuôi dạy con thông minh trong thời đại kĩ thuật số mà các bạn nhất định phải biết.  Có rất nhiều lí do để lí giải nguyên nhân tại sao cha mẹ lại cho trẻ tiếp xúc với các thiết bị công nghệ kĩ thuật số như: dỗ dành những cơn quấy khóc không dứt của con, giúp con học tiếng Anh nhanh hơn và tiện lợi hơn, và trên tất cả vẫn là bởi các thiết bị này có thể mang lại niềm vui cho con trẻ…'),
(N'MS149', N'Chờ Đến Mẫu Giáo Thì Đã Muộn', 'Ibuka Masaru', N'NXB Văn Học', N'Tâm lý giáo dục', 10, 69000, '/img/sach/cho-den-mau-giao-thi-da-muon.jpg', N'Chờ Đến Mẫu Giáo Thì Đã Muộn là cuốn sách bàn về phương pháp giáo dục trẻ trong giai đoạn từ 0 đến 3 tuổi của tác giả Ibuka Masaru, người sáng lập tập đoàn Sony đồng thời là một nhà nghiên cứu giáo dục. Dựa trên những nghiên cứu về sinh lý học của não bộ và di truyền học, ông đã khẳng định sự phát triển về trí tuệ và năng lực của trẻ được quyết định trong giai đoạn từ 0 đến 3 tuổi, giai đoạn này là “thời kỳ thích hợp” để “nuôi dạy một đứa trẻ trở nên ngoan ngoãn, vui vẻ, có trí tuệ thông minh và khỏe mạnh”. Từ đó, ông đưa ra những giải pháp để giúp các bậc cha mẹ tạo ra môi trường để trẻ có thể phát huy hết khả năng của mình.  Sức cuốn hút của Chờ Đến Mẫu Giáo Thì Đã Muộn không phải là những bài học giáo dục cao siêu mà ở lời nhắn nhủ hãy khơi dậy tiềm năng của trẻ từ những điều nhỏ nhặt trong cuộc sống bằng tình yêu của người làm cha mẹ.'),
(N'MS15', N'Đừng Bao Giờ Đi Ăn Một Mình', N'Keith Ferrazzi', N'NXB Trẻ', N'Kỹ năng sống', 10, 95000, '/img/sach/dung-bao-gio-di-an-1-minh.jpg', N'Công thức để làm được điều này, theo lời bậc thầy về kết nối là Keith Ferrazzi, chính là phải biết làm quen với mọi người. Ferrazzi đã khám phá từ khi còn trẻ rằng điểm khác biệt của những người thành công rực rỡ chính là cách họ vận dụng quyền năng của những mối quan hệ - để mọi người cùng thắng.  Trong quyển sách Đừng bao giờ đi ăn một mình, Ferrazzi chỉ ra từng bước cách thức – và lý do – mà chính ông đã áp dụng để kết nối với hàng ngàn người là đồng nghiệp, bạn bè có tên trong số danh bạ, những người ông đã giúp đỡ và ngược lại cũng sẵn sàng giúp đỡ ông. Ferrazzi sinh ra trong một gia đình có cha là công nhân nhà máy thép tỉnh lẻ và mẹ là lao công. Tuy vậy ông đã biết tận dụng khả năng kết nối xuất sắc của mình để dọn đường vào đến Yale, nhận bằng MBA tại Harvard, và giữ những chức vụ lãnh đạo quan trọng. Mặc dù chưa quá 40, Ferrazzi đã tạo được một mạng lưới trải dài từ hành lang ở Washington đến các tên tuổi lớn tại Hollywood. Ông đã được tạp chí Crain bình chọn là một trong số 40 người lãnh đạo kinh doanh dưới 40 tuổi đồng thời là Nhà lãnh đạo tương lai của thế giới tại Diễn đàn Kinh tế Thế giới ở Davos.  Phương pháp kết nối của Ferrazzi dựa trên nền tảng là sự rộng lượng, giúp đỡ bạn mình kết nối với những người bạn khác. Ferrazzi phân biệt rõ ràng giữa xây dựng mối quan hệ nghiêm túc với cách “tạo mạng lưới” vô tội vạ. Ông hệ thống hoá phương pháp của mình thành những nguyên tắc thực tiễn và đã được kiểm chứng như: Đừng ghi sổ: Kết nối không phải là cố gắng đạt được những gì mình muốn. Bạn phải đảm bảo những người quan trọng đối với bạn cũng đạt được điều họ muốn. Liên tục “pinging”: Nên nhớ phải liên tục liên hệ với những nối kết trong mạng lưới của bạn, không chỉ đợi đến khi bạn cần họ.'),
(N'MS150', N'Người Mẹ Tốt Hơn Là Người Thầy Tốt', N'Doãn Kiến Lợi', N'NXB Văn Học', N'Tâm lý giáo dục', 10, 139000, '/img/sach/nguoi-me-tot-hon-la-nguoi-thay-tot.jpg', N'Người Mẹ Tốt Hơn Là Người Thầy Tốt là cuốn sổ tay về giáo dục gia đình xuất sắc của chuyên gia giáo dục Doãn Kiến Lợi; là tác phẩm ngay từ khi còn chưa được xuất bản đã được lưu hành dưới hình thức “bản chép tay”.  Cuốn sách đã:  1. Đưa ra những nguyên tắc giáo dục gia đình hoàn toàn mới mẻ, giúp các bậc cha mẹ có cái nhìn toàn diện hơn về quá trình trưởng thành của con.  2. Đưa ra nhiều cách đơn giản mà hữu dụng, lý luận và thực tiễn kết hợp hoàn hảo với nhau, giúp các bậc phụ huynh có thể áp dụng và đem lại hiệu quả ngay lập tức.  3. Chỉ cho các bậc cha mẹ cách để giúp con mình yêu thích sự học, nâng cao thành tích; cách dạy trẻ làm người, luôn tự lập, tự cường, sống trách nhiệm.  Người Mẹ Tốt Hơn Là Người Thầy Tốt là một cuốn sách mô phạm thực sự cầu thị bàn về giáo dục gia đình, là công cụ thực dụng nhất của các bậc phụ huynh.'),
(N'MS151', N'Nuôi Dạy Con Theo Phương Pháp Montessori', N'Quốc Tú Hoa', N'NXB Phụ Nữ', N'Tâm lý giáo dục', 10, 75000, '/img/sach/cam-nang-nuoi-day-con-theo-phuong-phap-montessori.jpg', N'Hiện nay, phương pháp giáo dục Montessori đang dần trở nên phổ biến và được nhiều người đón nhận, đặc biệt là các bậc phụ huynh có con trong độ tuổi mầm non.   Tuy nhiên, giáo dục Montessori là như thế nào? Đâu là những yếu tố quan trọng trong giáo dục Montessori? Làm sao để tạo một môi trường Montessori tốt nhất cho trẻ?... là những câu hỏi mà không ít các bậc phụ huynh, thầy cô và những ai quan tâm tới giáo dục trẻ em còn băn khoăn thắc mắc.  Cuốn sách Cẩm Nang Nuôi Dạy Con Theo Phương Pháp Montessori giúp các bậc phụ huynh hiểu và nắm bắt có hiệu quả tinh thần thực sự của phương pháp giáo dục Montessori.   Hy vọng cuốn sách sẽ có ích cho những bậc phụ huynh trẻ, để ngày càng có thêm nhiều trẻ em được hưởng những điều tốt đẹp hơn.'),

(N'MS161', N'Y Học Thực Chứng', N'Nguyễn Văn Tuấn', N'NXB Tổng Hợp TP.HCM', N'Chăm sóc sức khỏe', 10, 145000, '/img/sach/y-hoc-thuc-chung1.jpg', N'Y học thực chứng là một trường phái thực hành y học dựa vào chứng cứ khoa học, kinh nghiệm của người thầy thuốc, và những giá trị liên quan đến bệnh nhân. Thực hành y học thực chứng đòi hỏi người thầy thuốc phải biết cách tìm, đánh giá và ứng dụng chứng cứ khoa học.  Cuốn sách nhỏ này giới thiệu các phương pháp truy tìm, thẩm định, xử lí và ứng dụng thông tin cho các mục đích lâm sàng. Bạn đọc sẽ tìm thấy khá nhiều khái niệm dịch tễ học và một số phương pháp phân tích số liệu thường thấy trong các bài báo y khoa trên các tạp san y học  quốc tế.'),
(N'MS162', N'Phế Đại Trường Trong Đông Y', N'Hoàng Duy Tân', N'NXB Dân Trí', N'Chăm sóc sức khỏe', 10, 180000, '/img/sach/phe-dai-truong-trong-dong-y.jpg', N'Nói đến không khí, mọi người đều nghĩ đến chức năng của tạng Phế - theo Nội kinh: “Phế chủ khí”.  Theo Đông y, Phế không chỉ chủ về khí nhưng còn nhiều công dụng khác như: liên quan đến da lông (Phế chủ bì mao), liên quan đến mũi (Phế khai khiếu ở mũi)… với những lý luận cao sâu mà người xưa đã dày công nghiên cứu để lại.    Nói đến Đại trường, người ta cũng chỉ nghĩ đến “ruột già” như 1 cơ quan có tác dụng bài tiết các chất thải, phân từ trong cơ thể ra bên ngoài. Có lẽ ít người quan tâm đến sự liên quan giữa Phế - Đại trường theo quan niệm Biểu Lý của Đông y. Đó là sự thiệt thòi về nhiều mặt. Thí dụ đối với Dưỡng sinh, người ta khuyên buổi sáng sớm là thời điểm rất thích hợp cho việc đại tiện. Tại sao vậy? Vì buổi sáng sớm 5-7 giờ là thời điểm của Phế khí. Khi Phế khí mạnh (theo vòng kinh khí tự nhiên) khí sẽ từ Phế chuyển xuống cho Đại trường (theo góc độ Biểu – Lý), sẽ giúp Đại trường tống phân và các chất cặn bã ra khỏi cơ thể một cách dễ dàng, hợp với tự nhiên…   Nhằm thừa kế, phát triển những nét độc đáo của Đông y đối với Phế, chúng tôi trích dịch, chọn lọc ra trong kho tàng y học đông phương những khía cạnh liên quan đến Phế và Đại trường, cũng như những sự liên quan khác như với Thận, với Tâm… giúp ích không nhỏ trong việc phòng và điều trị bệnh hữu hiệu hơn.'),
(N'MS163', N'Thực Hành Thiền Định', N'Matthieu Ricard', N'NXB Hà Nội', N'Chăm sóc sức khỏe', 10, 65000, '/img/sach/thuc-hanh-thien-dinh.jpg', N'Nếu như học thiền là một con đường mà ngay cả các bậc cao minh nhất đã theo đuổi trong suốt cuộc đời của họ thì thực hành thiền trong cuộc sống hàng ngày sẽ làm thay đổi cách nhìn của chúng ta về chính bản thân mình và thế giới. Đó là thông điệp mà cuốn sách Thực hành thiền định muốn gửi đến chúng ta. Cuốn sách vừa như vị thầy hướng dẫn tâm linh và triết học, vừa như người dắt dẫn cụ thể cho chúng ta cách thực hành thiền.  Mang trong mình hai nền văn hóa, bản thân lại có kinh nghiệm của người tu sỹ, hiểu biết những kinh sách thiêng liêng, được thường xuyên tiếp xúc với các vị thầy, Mathieu Ricard cho chúng ta thấy đặc tính chung của một môn phái thiền xây dựng trên nền tảng là tình thương yêu đồng loại, sự cảm thông và phát triển các phẩm chất của con người. Ông cũng hé lộ cho ta thấy những phúc lạc đương nhiên mà thiền có khả năng mang lại cho mỗi người trong một xã hội hiện tại vốn mang đậm tính cá nhân và vật chất, giúp chúng ta khám phá và vun trồng những khát vọng sâu xa nhất của bản thân.'),
(N'MS164', N'Dưỡng Sinh', N'Nguyễn Mạnh Trí', N'NXB Tổng Hợp TP.HCM', N'Chăm sóc sức khỏe', 14, 55000, '/img/sach/duong-sinh-bia.jpg', N'Tập dưỡng sinh rất có lợi cho hệ hô hấp của người cao tuổi. Khi tập luyện, người cao tuổi sẽ giữ được khả năng thở sâu và đều, tránh tình trạng hụt hơi khó thở khi về già. Theo các nghiên cứu, việc hít thở sâu và đều sẽ giúp khí huyết trong cơ thể lưu thông ổn định giúp ích rất nhiều cho quá trình tuần hoàn trao đổi  Khí – Máu. Ngoài ra, việc điều hòa hơi thở còn giúp cho hệ thần kinh hoạt động tốt hơn làm giảm những triệu chứng mệt mỏi, căng thẳng và đặc biệt là chứng hay quên ở người già.  '),
(N'MS165', N'Sức Khỏe Gia Đình', N'Đỗ Hồng Ngọc', N'NXB Tổng Hợp TP.HCM', N'Chăm sóc sức khỏe', 14, 90000, '/img/sach/suc-khoe-gia-dinh.jpg', N'Ta đang sống trong một thời đại \"ngộ nghĩnh\". Thực phẩm béo bổ ê hề, sẵn sàng dụ dỗ ta làm cho ta… béo phệ để sinh ra vô số bệnh tật, rồi cạnh đó, lại mở ra nhiều bệnh viện, nhà thuốc để cứu vớt ta, chăm sóc chữa trị cho ta. Cũng vậy, bia rượu thuốc lá tràn lan, sẵn sàng dụ dỗ ta làm cho ta gặp đủ thứ tai nạn thương tích, ung thư này nọ rồi cạnh đó tổ chức cấp cứu chấn thương, đặt thêm giường bệnh… Cuộc sống tốc độ, đầy cạnh tranh, căng thẳng, mọi người hùng hục lao vào kiếm tiền cho thật nhiều, phung phí sức khỏe, để rồi dùng tiền đó phục hồi sức khỏe.  Y học ngày càng tiến bộ, kỹ thuật ngày càng cao, thuốc men ngày càng nhiều thì bệnh tật cũng ngày càng phát triển, gia tăng, đôi khi đe dọa cả hành tinh.  Các nghiên cứu cho thấy khi fastfood tiến từ Bắc Mỹ xuống Nam Mỹ thì đồng thời béo phì và bệnh tật cũng tăng theo tỷ lệ thuận. Khi thuốc lá, rượu bia bị hạn chế, cấm ngặt ở các nước phát triển thì họ đẩy mạnh sản xuất ở các nước nghèo khác.'),
(N'MS166', N'Giải tích 1', N'Lê Hoàng Tuấn', N'NXB ĐHQG TP.HCM', N'Giáo trình', 49, 75000, '/img/sach/images.png', N'Khi ngày càng nhiều người truy cập Web từ điện thoại và các thiết bị di động khác, các nhà phát triển Web cần biết cách xây dựng trang Web có khả năng đáp ứng tốt cho các thiết bị này. Muốn vây, họ cần học những kỹ thuật, nghiên cứu những vấn đề mang tính đặ thù liên quan tới việc phân phối nội dung Web cho thiết bị di động. Nếu bạn là nhà phát triển và thiết kế Web đang bắt đầu tìm hiểu về Web trên di ddoonngj thì đây chính là cuốn sách dành cho bạn.  Phát triển Web cho smartphone giới thiệu về cú pháp, ngữ nghĩa và hệ sinh thái của Web trên di động. Cuốn sách hướng dẫn cách xây dựng những trang Web trên di động có khả năng thích ứng, hồi đáp và đạt các tiêu chuẩn giúp đảm bảo trang Web chạy được trên mọi trình duyệt di động. Bạn đọc cũng sẽ nghiên cứu thuộc tính đặc trưng của thiết bị, hệ sinh thái và người dùng di động, từ đó biết cách áp dụng những phương pháp kỹ thuật tốt nhất vào việc phát triển Web cho thiết bị di động.'),
(N'MS167', N'Giải tích 2', N'Lê Hoàng Tuấn', N'NXB ĐHQG TP.HCM', N'Giáo trình', 50, 80000, '/img/sach/download.png', N'Khi ngày càng nhiều người truy cập Web từ điện thoại và các thiết bị di động khác, các nhà phát triển Web cần biết cách xây dựng trang Web có khả năng đáp ứng tốt cho các thiết bị này. Muốn vây, họ cần học những kỹ thuật, nghiên cứu những vấn đề mang tính đặ thù liên quan tới việc phân phối nội dung Web cho thiết bị di động. Nếu bạn là nhà phát triển và thiết kế Web đang bắt đầu tìm hiểu về Web trên di ddoonngj thì đây chính là cuốn sách dành cho bạn.  Phát triển Web cho smartphone giới thiệu về cú pháp, ngữ nghĩa và hệ sinh thái của Web trên di động. Cuốn sách hướng dẫn cách xây dựng những trang Web trên di động có khả năng thích ứng, hồi đáp và đạt các tiêu chuẩn giúp đảm bảo trang Web chạy được trên mọi trình duyệt di động. Bạn đọc cũng sẽ nghiên cứu thuộc tính đặc trưng của thiết bị, hệ sinh thái và người dùng di động, từ đó biết cách áp dụng những phương pháp kỹ thuật tốt nhất vào việc phát triển Web cho thiết bị di động.'),
(N'MS168', N'Lý Luận Nhà Nước Và Pháp Luật', N'Lê Vũ Nam', N'NXB ĐHQG TP.HCM', N'Giáo trình', 20, 96000, '/img/sach/download.jpg', N'Tập dưỡng sinh rất có lợi cho hệ hô hấp của người cao tuổi. Khi tập luyện, người cao tuổi sẽ giữ được khả năng thở sâu và đều, tránh tình trạng hụt hơi khó thở khi về già. Theo các nghiên cứu, việc hít thở sâu và đều sẽ giúp khí huyết trong cơ thể lưu thông ổn định giúp ích rất nhiều cho quá trình tuần hoàn trao đổi  Khí – Máu. Ngoài ra, việc điều hòa hơi thở còn giúp cho hệ thần kinh hoạt động tốt hơn làm giảm những triệu chứng mệt mỏi, căng thẳng và đặc biệt là chứng hay quên ở người già.  '),
(N'MS169', N'Tư Tưởng Hồ Chí Minh', N'Bộ Giáo Dục', N'NXB Chính Trị QG', N'Giáo trình', 100, 22000, '/img/sach/giao-trinh-tu-tuong-ho-chi-minh.jpg', N'Thực hiện các Nghị quyết của Đảng Cộng sản Việt Nam, nhất là Nghị quyết Trung ương 5 (khóa X) về công tác tư tưởng, lý luận và báo chí trước yêu cầu mới, ngày 18/9/2008, Bộ Giáo dục và Đào tạo đã ban hành Quyết định số 52/2008/QĐ-BGĐT ban hành Chương trình môn học Tư Tưởng Hồ Chí Minh dành cho sinh viên khối không chuyên ngành Mác - Lênin, tư tưởng Hồ Chí Minh và phối hợp với Nhà xuất bản Chính trị quốc gia Sự thật xuất bản Giáo Trình Tư Tưởng Hồ Chí Minh (Dành cho sinh viên đại học, cao đăng khối không chuyên ngành Mác - Lênin, tư tưởng Hồ Chí Minh)  Cuốn giáo trình chính trị này bao gồm 8 chương: • Đối tượng, phương pháp nghiên cứu và ý nghĩa học tập môn tư tưởng Hồ Chí Minh • Cơ sở, quá trình hình thành và phát triển tư tưởng Hồ Chí Minh • Tư tưởng Hồ Chí Minh về vấn đề dân tộc và cách mạng giải phóng dân tộc • Tư tưởng Hồ Chí Minh về chủ nghĩa xã hội và con đường quá độ lên chủ nghĩa xã hộ ở Việt Nam  • Tư tưởng Hồ Chí Minh về Đảng Cộng Sản Việt Nam • Tư tưởng Hồ Chí Minh về đại đoàn kết dân tộc và đoàn kết quốc tế  • Tư tưởng Hồ Chí Minh về xây dựng nhà nước của dân, do dân, vì dân • Tư tưởng Hồ Chí Minh về văn hóa, đạo đức và xây dựng con người mới'),
(N'MS17', N'Mặc Đẹp Để Thành Công', N'Nishimura Yukiko', N'NXB Thế Giới', N'Kỹ năng sống', 10, 99000, '/img/sach/macdepdethanhcong.jpg', '  Trong buổi phát biểu chính thức ngay sau thất bại ở kỳ bầu cử Tổng thống Mỹ lần thứ 45, cựu Ngoại trưởng Mỹ Hilary Clinton đã xuất hiện trong một bộ trang trang phục màu tím sẫm, sát cánh bên bà – cựu tổng thống Bill Clinton – hộ tống vợ trong một bộ suit màu đen lịch lãm, đi kèm chiếc cà vạt bằng lụa cũng một màu tím sẫm. Sắc tím trên bộ trang phục của cặp vợ chồng nổi tiếng khi đó tượng trưng cho sự hòa hợp, hòa giải và tái hợp tác – một thông điệp hết sức cầu thị và văn minh ngay cả khi chiến thắng chung cuộc không thuộc về phe họ.   Câu chuyện này chỉ là một trong số rất nhiều ví dụ về việc những nhân vật chính trị quyền lực, những doanh nhân thành đạt bậc nhất, những ngôi sao màn bạc và những người nổi tiếng… là những người luôn biết cách “lợi dụng” ngoại hình (mà cụ thể ở đây là phục trang), như một trong những “phát ngôn viên” vô hình mà đắc lực. Hơn cả công dụng che đậy cơ thể hay đơn giản chỉ-để-làm-đẹp, phục trang là một chiếc danh thiếp mà chúng ta vô thức chìa ra với thế giới ngay cả khi ta chưa kịp giới thiệu tên họ mình.   Một người đàn ông biết ăn mặc một cách chỉn chu lịch lãm sẽ gợi cảm giác anh là người mạnh mẽ và đáng tin. Một người phụ nữ biết chọn trang phục phù hợp với vóc dáng của mình sẽ tạo cảm giác cô thật tinh tế và khéo léo. Một chàng trai trẻ mới đi làm sẽ đạt được nhiều sự chú ý hơn nếu anh biết phục trang một cách gọn ghẽ và tự tin. Một vị quản lý dường như có uy thế hơn hẳn nếu hôm nay ông mặc suit – món đồ biểu tượng cho quyền lực nam tính của người đàn ông… Trong phần lớn những cuộc tiếp xúc lần đầu, ngoại hình của bạn là thứ tác động nhanh chóng, mạnh mẽ và trực tiếp lên đánh giá của người đối diện. Nếu bạn ăn mặc như thế mình là một nhân vật đáng trọng, tất yếu người khác sẽ phải lựa chọn thái độ tương xứng khi đối diện với bạn.  '),
(N'MS170', N'Đường Lối Cách Mạng Của Đảng Cộng Sản VN', N'Bộ Giáo Dục', N'NXB Chính Trị QG', N'Giáo trình', 100, 30000, '/img/sach/giao-trinh-duong-loi-cah-mang-cua-dang-cong-san-viet-nam.jpg', N'Thực hiện các Nghị quyết của Đảng Cộng sản Việt Nam, nhất là Nghị quyết Trung ương 5 (khóa X) về công tác tư tưởng, lý luận và báo chí trước yêu cầu mới, ngày 18/9/2008, Bộ Giáo dục và Đào tạo đã ban hành Quyết định số 52/2008/QĐ-BGĐT ban hành Chương trình môn học Đường Lối Cách Mạng Của Đảng Cộng Sản Việt Nam dành cho sinh viên khối không chuyên ngành Mác - Lênin, tư tưởng Hồ Chí Minh và phối hợp với Nhà xuất bản Chính trị quốc gia Sự thật xuất bản Giáo Trình Đường Lối Cách Mạng Của Đảng Cộng Sản Việt Nam (Dành cho sinh viên đại học, cao đăng khối không chuyên ngành Mác - Lênin, tư tưởng Hồ Chí Minh)  Cuốn giáo trình chính trị này bao gồm 8 chương: ♦ Sự ra đời của Đảng Cộng Sản Việt Nam và Cương lĩnh chính trị đầu tiên của Đảng ♦ Đường lối đấu tranh giành chính quyền ♦ Đường lối kháng chiến chống thực dân Pháp và đế quốc Mỹ xâm lược (1945 - 1975) ♦ Đường lối công nghiệp hóa ♦ Đường lối xây dựng nền kinh tế thị trường định hướng xã hội chủ nghĩa ♦ Đường lối xây dựng hệ thống chính trị ♦ Đường lối xây dựng, phát triển nền văn hóa và giải quyết các vấn đề xã hội ♦ Đường lối đối ngoại'),
(N'MS171', N'Phát Triển Ứng Dụng Web', N'Trần Khánh Lâm', N'NXB ĐHQG TP.HCM', N'Giáo trình', 50, 56000, '/img/sach/ea99106193d3cab8cd9227476cb960cf.gif', N'Khi ngày càng nhiều người truy cập Web từ điện thoại và các thiết bị di động khác, các nhà phát triển Web cần biết cách xây dựng trang Web có khả năng đáp ứng tốt cho các thiết bị này. Muốn vây, họ cần học những kỹ thuật, nghiên cứu những vấn đề mang tính đặ thù liên quan tới việc phân phối nội dung Web cho thiết bị di động. Nếu bạn là nhà phát triển và thiết kế Web đang bắt đầu tìm hiểu về Web trên di ddoonngj thì đây chính là cuốn sách dành cho bạn.  Phát triển Web cho smartphone giới thiệu về cú pháp, ngữ nghĩa và hệ sinh thái của Web trên di động. Cuốn sách hướng dẫn cách xây dựng những trang Web trên di động có khả năng thích ứng, hồi đáp và đạt các tiêu chuẩn giúp đảm bảo trang Web chạy được trên mọi trình duyệt di động. Bạn đọc cũng sẽ nghiên cứu thuộc tính đặc trưng của thiết bị, hệ sinh thái và người dùng di động, từ đó biết cách áp dụng những phương pháp kỹ thuật tốt nhất vào việc phát triển Web cho thiết bị di động.'),
(N'MS172', N'Phân Tích Thiết Kế HTTT', N'Phạm Nguyễn Cương', N'NXB ĐHQG TP.HCM', N'Giáo trình', 25, 90000, '/img/sach/Giao-trinh-HTTT-huong-doi-tuong-min.jpg', N'Giáo trình Phân tích thiết kế hệ thống thông tin hướng đối tượng được viết để phục vụ cho sinh viên theo học môn học Phân tích thiết kế hệ thống thông tin, thuộc chuyên ngành hệ thống thông tin (HTTT) tại khoa Công nghệ thông tin, trường Đại học Khoa học Tự nhiên (Đại học Quốc gia Thành phố Hồ Chí Minh).  Nội dung của giáo trình cung cấp các khái niệm cơ bản về HTTT, các nền tảng và phương pháp hình thành và xây dựng HTTT trong các đơn vị tổ chức. Trong đó tập trung vào chi tiết các kiến thức và kỹ năng về các giai đoạn phân tích và thiết kế hệ thống. Đối với giai đoạn phân tích, chúng tôi trình bày cách một dự án HTTT được hình thành và chọn lựa đầu tư trong doanh nghiệp, các kỹ thuật phân tích nghiệp vụ và thu thập yêu cầu, mô hình hóa nội dung nghiệp vụ và yêu cầu sử dụng ngôn ngữ UML. Đối với kiến thức thiết kế, thiết kế kiến trúc và triển khai. Với các kiến thức trên, các bạn có thể vận dụng để rèn luyện các kỹ năng phân tích thiết kế trong các đồ án môn học, cũng như áp dụng nó vào xây dựng các dự án HTTT cho các doanh nghiệp sau này.  Ngoài chúng tôi cũng dành một phần của giáo trình để mở rộng đến các bạn một kiến thức thiết thực không kém phần quan trọng để vận hành thành công HTTT tự động hóa. Đó là các phương pháp triển khai hệ thống thực tế một cách hiệu quả, các lựa chọn huấn luyện và đào tạo người dùng và quản lý thay đổi giúp chuyển từ hệ thống cũ sang hệ thống mới thành công. '),
(N'MS173', N'Nhập Môn Mạng Máy Tính', N'Nguyễn Bình Dương', N'NXB ĐHQG TP.HCM', N'Giáo trình', 25, 30000, '/img/sach/13_0.jpg', N'Mạng máy tính là một hệthống các máy tính tựtrị(Autonomous  Computer) được kết nối với nhau bới các đường truyền vật lý và  theo một kiến trúc nào đó.  z Từnhững năm 70 bắt đầu xuất hiện khái niệm mạng truyền thông  (Communication Network) trong đó các thành phần chính của  mạng là các nút mạng, được gọi là bộchuyển mạch (Switching  Unit) dùng đểhướng thông tin tới đích. Các nút mạng được nối  với nhau bằng các đường truyền (Communication Subnet hay  Communication Line).'),
(N'MS174', N'Lập Trình JaVa', N'Phan Hoàng Khải', N'NXB ĐHQG TP.HCM', N'Giáo trình', 20, 99000, '/img/sach/anhnen.png', N' Quyển sách này nói về điều gì?  JAVA là ngôn ngữ lập trình rất phổ biến nhất hiện nay, học #JAVA_CORE bạn sẽ có rất nhiều hướng đi, từ lập trình Mobile app, Java web, Desktop App, Game, và tất cả đều sử dụng nền tảng của JAVA CORE. Quyển sách này gồm 22 bài học từ Tư duy LTHĐT(Đa hình, kế thừ) đến các đối tượng #JavaCore (String, Array, File), lập trình giao diện Swing. Quyển sách này sẽ giúp bạn:  Đi vào thế giới lập trình hết sức tự nhiên, thân thiện và dễ hiểu - LẬP TRÌNH HƯỚNG ĐỐI TƯỢNG LÀ TƯ DUY GẮN LIỀN VỚI CUỘC SỐNG HẰNG NGÀY Nắm vững được thế nào là tư duy lập trình hướng đối tượng và cách phân tích một bài toán lập trình Hiểu được các khái niệm lập trình Java cơ bản. Thực hành xây dựng được các giao diện phần mềm desktop bằng ngôn ngữ JAVA Sau khi có được nền tảng kiến thức lập trình JAVA core bạn có thể tự học các ngôn ngữ lập trình hướng đối tượng khác như C++/C, Python, III. Quyển sách này dành cho ai?  Là sách tham khảo, hướng dẫn tự học lập trình hướng đối tượng bằng ngôn ngữ JAVACore Dành cho người mới bắt đầu học lập trình, sinh viên chưa vững tư duy LTHĐT, Java core Dành cho người mất gốc hoặc trái ngành muốn học lập trình'),
(N'MS175', N'Hệ Điều Hành', N'Chung Quang Khánh', N'NXB ĐHQG TP.HCM', N'Giáo trình', 60, 40000, '/img/sach/hệ-điều-hành-Linux.jpg', N'Kiến thức về hệ điều hành Linux khá rộng, quyển giáo trình này mong muốn giới thiệu đến độc giả những kiến thức căn bản nhất để có thể từng bước làm quen từ việc sử dụng HĐh Linux đến cấu hình các dịch vụ mạng để hiểu rõ hơn về lý thuyết hệ điều hành, mạng máy tính và những điều tuyệt với khác mà HĐH Linux mang lại.'),
(N'MS176', '600 Essential Words For The Toeic', N'Dr. Lin Lougheed', N'NXB Tổng Hợp TP.HCM', N'Ngoại ngữ', 10, 110000, '/img/sach/600-essential-word-for-the-toeic.jpg', N'600 Essential Words For The Toeic của tác giả  Dr. Lin Lougheed  giúp bạn nắm vững những nền tảng cơ bản để hiểu những ngữ cảnh đặc biệt thường gặp trong một bài thi TOEIC. Từ vựng trong sách được phân chia thành từng chương tập hợp thành những chủ đề với ngữ cảnh cụ thể, giúp người đọc dễ dàng hệ thống được từ vựng. Những từ này không phải là từ chuyên môn, mà là những từ vựng thông dụng có thể dùng được trong rất nhiều ngữ cảnh khác nhau. Tuy nhiên, vì được sử dụng trong một ngữ cảnh chuyên môn, nên ngữ nghĩa của chúng sẽ thay đổi so với khi sử dụng trong những tình huống phổ thông. Hiểu được điều này sẽ giúp bạn cải thiện đáng kể kết quả thi TOEIC.  600 từ vựng được trình bày ở 50 bài học và thực hành chính. Quyển sách còn hướng dẫn bạn cách xây dựng vốn từ như: phân tích các thành tố từ, nhận dạng các hình thái ngữ pháp, nhận dạng họ từ, tạo từ điển cá nhân, viết nhật ký đọc hàng ngày. Bên cạnh đó, đĩa CD được đính kèm trong quyển sách sẽ hỗ trợ bạn ghi nhớ từ vựng hiệu quả hơn.  Cuốn sách Toeic  này sẽ giúp bạn cải thiện vốn từ vựng của mình và ôn luyện tốt hơn trong quá trình tiếp thu và giao tiếp trong kì thi Toeic, đạt được điểm tối đa trong kì thi Toeic.'),
(N'MS177', N'Starter Toeic Third Edition', N'Anne Taylor', N'NXB Tổng Hợp TP.HCM', N'Ngoại ngữ', 15, 120000, '/img/sach/starter-toeic.jpg', N'Starter Toeic TOEIC Third Edition dành cho những người mới bắt đầu chuẩn bị cho kì thi TOEIC.  Cuốn sách cung cấp một nền tảng tuyệt vời cho sinh viên bắt đầu chuẩn bị cho kì thi TOEIC.   Nội dung:   Thông tin chung về kì thi TOEIC Lời khuyên cho từng phần của bài kiểm tra Giải thích ngữ pháp rõ ràng và chính xác Thực hành ngữ pháp tổng hợp Bài Test Một bài kiểm tra Toeic đầy đủ Đáp án chi tiết Đĩa nghe phần Listen với phát âm chuẩn   Đây chính là cuốn sách được tổng hợp biên soạn và cập nhật dựa theo những yêu cầu mới của các kỳ thi TOEIC, đồng thời cũng để đáp ứng nhu cầu học và  luyện thi Toeic  hiện đại. Mỗi bộ sách được chia thành nhiều cấp độ, từ sơ cấp đến nâng cao, phù hợp với mọi trình độ học viên, thuận lợi cho giáo viên trong việc tổ chức học, ra đề thi và sắp lớp đúng trình độ để việc học tập và kiểm tra của học viên đạt kết quả mong muốn. Các học viên có thể tự kiểm tra và chọn cho mình cấp độ phù hợp với khả năng.'),
(N'MS178', N'Luyện Thi Toeic 850 Reading', N'Jo Gang - Soo', N'NXB Khoa Học Xã Hội', N'Ngoại ngữ', 10, 169000, '/img/sach/luyen-thi-toeic-850-reading-tai-ban-2018.gif', N'5 TIẾNG MỖI NGÀY, ĐẠT NGAY 850 ĐIỂM  Luyện thi TOEIC 850 Reading nói riêng và bộ TOEIC của Jo Gang-Soo nói chung, có nhiều ưu điểm hơn so với những cuốn sách luyện thi TOEIC khác từng xuất bản.  1. Dũng cảm phá bỏ quan niệm \"lý thuyết là cơ bản\"!  Dù học thuộc lòng hết lý thuyết bao la, không sai một giới từ thì liệu điểm thi của bạn có thể tăng lên không? Chỉ lý thuyết thôi chắc chắn là không đủ, phải biết vận dụng lý thuyết vào thực tiễn. Để ngăn việc học lý thuyết một cách rời rạc, vô phương hướng, giáo trình này sẽ tập trung vào những lý thuyết thường xuất hiện trong đề thi TOEIC.  2. Không giải thích dài dòng! Mọi lý thuyết đều có ví dụ và đề thi mẫu!  Hầu hết sách giáo trình đều đưa ra những diễn giải hết sức dài dòng. Cuốn sách này sẽ trình bày lý thuyết thông qua những câu ví dụ và đề thi mẫu thực tế. Nhờ đó, thí sinh không chỉ rút ngắn thời gian ôn luyện mà còn nâng cao năng lực vận dụng lý thuyết vào bài thi.  3. Xu hướng ra đề mới nhất!  Người viết sách không chỉ chú trọng vào nội dung ôn luyện về số lượng đơn thuần mà còn nỗ lực nâng cao chất lượng ôn luyện trong từng bài học. Ngoài ra, giáo trình còn đề cập tới cả những cấu trúc, dấu hiệu dễ gây nhầm lẫn cũng như trình bày một phần riêng về phương pháp đạt điểm cao, để giúp thí sinh giảm thiểu sai sót khi làm bài.  4. Phân tích chi tiết cách làm Phần 7!  Phần 7 luôn là phần khiến các thí sinh vất vả và tốn nhiều thời gian nhất. Bởi vậy, giáo trình này phân tích đầy đủ 48 câu hỏi và tổng hợp thành những phương pháp làm bài hiệu quả để các bạn có thể áp dụng ngay vào bài thi. Chỉ cần ghi nhớ những phương pháp này và luyện tập trong vòng một tháng, các bạn có thể vượt qua được phần thi khó nhằn này sớm hơn ít nhất là 5 phút. Và như các bạn đều biết, mấu chốt để đạt điểm thi TOEIC cao không nằm đâu khác mà ở chính phần này.'),
(N'MS179', N'Luyện Thi Toeic 750 Reading', N'Jo Gang - Soo', N'NXB Khoa Học Xã Hội', N'Ngoại ngữ', 10, 149000, '/img/sach/luyen-thi-toeic-750-reading-tai-ban.jpg', N' 5 TIẾNG MỖI NGÀY, ĐẠT NGAY 750 ĐIỂM Cung cấp đề thi và mẫu câu phong phú đã dạng Phân tích chi tiết, hiệu quả cách làm phần 7 HIỆU QUẢ TỨC THÌ - TỰ TIN HOÀN THÀNH BÀI THI TOEIC Dũng cảm phá bỏ quan niệm \" lý thuyết là cơ bản \" : Dù học thuộc lòng hết lý thuyết bao la, không sai một giới từ thì liệu điểm thi của bạn có thể tăng lên không? Chỉ lý thuyết thôi chắc chắn là không đủ, phải biết vận dụng lý thuyết vào thực tiễn. Để ngăn việc học lý thuyết một cách rời rạc, vô phương hướng, giáo trình này sẽ tập trung vào những lý thuyết thường xuất hiện trong đề thi TOEIC. Không giải thích dài dòng! Mọi lý thuyết đều có ví dụ và đề thi mẫu!: Hầu hết sách giáo trình đều đưa ra những diễn giải hết sức dài dòng. Cuốn sách này sẽ trình bày lý thuyết thông qua những câu ví dụ và đề thi mẫu thực tế. Nhờ đó, thí sinh không chỉ rút ngắn thời gian ôn luyện mà còn nâng cao năng lực vận dụng lý thuyết vào bài thi. Xu hướng ra đề mới nhất: Người viết sách không chỉ chú trọng vào nội dung ôn luyện về số lượng đơn thuần mà còn nỗ lực nâng cao chất lượng ôn luyện trong từng bài học. Ngoài ra, giáo trình còn đề cập tới cả những cấu trúc, dấu hiệu dễ gây nhầm lẫn cũng như trình bày một phần riêng về phương pháp đạt điểm cao, để giúp thí sinh giảm thiểu sai sót khi làm bài. Phân tích chi tiết cách làm phần 7: Phần 7 luôn là phần khiến các thí sinh vất vả và tốn nhiều thời gian nhất. Bởi vậy, giáo trình này phân tích đầy đủ 48 câu hỏi và tổng hợp thành những phương pháp làm bài hiệu quả để các bạn có thể áp dụng ngay vào bài thi. Chỉ cần ghi nhớ những phương pháp này và luyện tập trong vòng một tháng, các bạn có thể vượt qua được phần thi khó nhằn này sớm hơn ít nhất là 5 phút. Và như các bạn đều biết, mấu chốt để đạt điểm thi TOEIC cao không nằm đâu khác mà ở chính phần này.'),
